package com.example.jogaii.data

import androidx.room.Database
import androidx.room.RoomDatabase
import androidx.sqlite.db.SupportSQLiteDatabase
import com.example.jogaii.R
import com.example.jogaii.di.ApplicationScope
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.launch
import javax.inject.Inject
import javax.inject.Provider

@Database(entities = [Asana::class,Type::class], version = 1)
abstract class AsanaDatabase : RoomDatabase() {

    abstract fun asanaDao() : AsanaDao

    class Callback @Inject constructor(
        private val database: Provider<AsanaDatabase>,
        @ApplicationScope private val applicationScope: CoroutineScope
    ) : RoomDatabase.Callback() {
        override fun onCreate(db: SupportSQLiteDatabase) {
            super.onCreate(db)
            val dao = database.get().asanaDao()

            applicationScope.launch{
                dao.insert(Type("równowaga ramion",R.drawable.eka_hasta_vrksasanav))
                dao.insert(Type("wygięcia do tyłu",R.drawable.eka_pada_rajakapotasana_ii))
                dao.insert(Type("równowaga",R.drawable.garudasana))
                dao.insert(Type("oddychanie",R.drawable.vajrasana))
                dao.insert(Type("otwieranie klatki",R.drawable.ustrasana))
                dao.insert(Type("skłony do przodu",R.drawable.parsvottanasana))
                dao.insert(Type("otwieranie bioder",R.drawable.malasanaa))
                dao.insert(Type("skręty",R.drawable.katichakrasana))
                dao.insert(Type("siłowe",R.drawable.adho_mukha_svanasana))

                dao.insert(Asana("Viparita Shalabhasana", "Superman", "Aby poprawnie wykonać figurę, połóż się brzuchem na macie. Następnie postaraj się unieść szyję, głowę, ręce i nogi do góry", 2,1, R.drawable.viparita_shalabhasana,false))
                dao.insert(Asana("Virasana", "Bohater", "Klęknij łącząc kolana i rozszerzając stopy na bok. Grzbiety stóp są na podłodze ustawione w osi z goleniami. Odległość miedzy piętami taka żeby pośladki zmieściły się między piętami. \nKolana blisko siebie uda równolegle do siebie. Pośladki pomiędzy piętami - ustaw miednicę prostopadle do podłogi. Palce stóp szeroko - grzbiety stóp naciskają na podłogę. \nWyciągnij kręgosłup - wyprostuj plecy. Dłonie oparte na udach - mostek w górę - barki w dół. Jeśli nie możesz usiąść na podłodze podeprzyj pośladki na kocu lub klocku. Opuść kość ogonową w dół i wyciągnij mostek do góry. Łopatki do środka pleców. Grzbiety stóp i kolana naciskają na podłogę.", 7,1, R.drawable.virasana,true))
                dao.insert(Asana("Salambhasana", "Świerszcz", "Połóż się na brzuchu na podłodze z wyciągniętymi połączonymi nogami. Połącz kostki, wyprostuj kolana. Oprzyj czoło na podłodze, wydłuż tylną część szyi. Połóż ręce wzdłuż tułowia, wnętrza dłoni skieruj do sufitu.\nRozluźnij ciało. Zrotuj uda do wewnątrz, ściągnij pośladki. Kość łonowa powinna lekko dociskać do podłogi. \nPowolnym ruchem wyciągnij ramiona do tyłu, kierując ruch w stronę stóp. Ramiona powinny znajdować się równolegle do podłogi. Ściągnij łopatki do środka, otwierając tym samym klatkę piersiową. Skręć ramiona do wewnątrz. \nNa wdechu podnieś wyżej klatkę piersiową, a potem unieś silne połączone nogi nad podłogę, staraj się nie rozłączać stóp i nie uginać kolan. Duże paluchy stóp powinny być do siebie zwrócone. Oddychaj spokojnie. Jeżeli czujesz napięcie w lędźwiach nie próbuj podnosić nóg i tułowia wyżej. \nZachowaj rozluźnioną szyję i barki. Zwróć głowę lekko ku górze lub jeśli sprawia ci to trudność patrz na wprost.", 2,1, R.drawable.salambhasana,false))
                dao.insert(Asana("Ardha Bhekasana", "Półżaba", "Połóż się na brzuchu twarzą do podłogi. Połóż dłonie na podłodze i wyciągnij się na nich do góry. Unieś głowę w stronę sufitu. \nZegnij prawe kolano i przyciągnij je do prawego pośladka. Połóż lewe przedramię na podłodze a prawą dłoń połóż na prawej stopie, tuż przy krawędzi pośladka. Balansuj na lewym przedramieniu opierając je mocno o podłogę. W tym samym czasie prawa ręka jest zgięta, łokieć zwrócony w stronę sufitu. Zakręć palce dłoni wokół palców stopy, wnętrzem dłoni naciskając na jej zewnętrzną część.\n Na wydechu zacznij dociskać dłonią stopę w kierunku zewnętrznej krawędzi pośladka, kierując ruch w stronę podłogi. Upewnij się, że kolano znajduje się w jednej linii z biodrem. Jeżeli boli cię kolano, nie dociskaj zbyt mocno. \nWyprostuj lewą rękę przed sobą, balansuj na mocno wciśniętej w matę lewej dłoni, utrzymaj mocną rękę, prosty. Wyciągnij klatkę piersiową do góry. Unieś talię, rozciągaj brzuch.", 2,1, R.drawable.ardha_bhekasana,true))
                dao.insert(Asana("Advasana", "Odwrócony trup", "Połóż się na brzuchu. \nWyciągnij obie ręce do przodu po obu stronach głowy. \nRozluźnij całe ciało w taki sam sposób, jak opisano dla Shavasany.", 4,1, R.drawable.advasana,false))
                dao.insert(Asana("Ananda Balasana", "Szczęśliwe dziecko", "Połóż się na plecach. Na wydechu zegnij kolana i przyciągnij je do brzucha. \nNa wdechu chwyć dłońmi zewnętrzne części stóp (jeżeli to sprawia ci problem użyj paska zawijając pętle wokół stóp). \nNieznacznie otwórz kolana, tak aby znajdowały się szerzej niż tułów a następnie wyciągnij je w górę, w kierunku pach. \nZadbaj o to aby kostki znajdowały się tuż nad kolanami, tak aby golenie były umieszczone prostopadle do podłogi. Naciągnij stopy. Opuszczając ręce do dołu, jednocześnie delikatnie unoś stopy w kierunku dłoni - w ten sposób powstanie opór. \nZbliżaj uda do tułowia i w kierunku podłogi wydłużając kręgosłup. Rozluźnij kość ogonową, niech spoczywa na podłodze. Wydłuż tylną część szyi.", 7,1, R.drawable.ananda_balasana,false))
                dao.insert(Asana("Matsyasana", "Ryba", "Połóż się na plecach. Upewnij się, że Twoje nogi są razem, a twoje dłonie są umieszczone obok ciała.\nUmieść dłonie pod biodrami tak, aby wewnętrzna część skierowana była w stronę ziemi. Teraz przysuń łokcie bliżej siebie, zbliżając je do talii.\nUnieś głowę i górną część tułowia. Unieś swoje ramiona i barki do góry, w taki sposób, aby klatka piersiowa miała kształt łuku. Patrz przed siebie i unieś podbródek do góry. \nUpewnij się, że ciężar Twojego ciała jest na łokciach, a nie na głowie. \nWyprostuj i złącz nogi, uda muszą być przez cały czas napięte i aktywne. Jeżeli ta pozycja jest za trudna, możesz zgiąć kolana.", 2,1, R.drawable.matsyasana,false))
                dao.insert(Asana("Supta Baddha Konasana", "Odpoczywająca bogini", "Połóż się na plecach. Zegnij kolana w taki sposób, aby zewnętrzne części stóp połączyły się ze sobą. Rozluźnij barki. Dłonie możesz położyć na brzuchu albo całe ręce unieść do góry i odłożyć wyprostowane za głową", 7,1, R.drawable.supta_baddha_konasana,false))
                dao.insert(Asana("Pavanamuktasana", "Łagodzący wiatr", "Połóż się na plecach. Zegnij nogi w kolanach i przyciągnij je do siebie. Następnie oderwij głowę od maty i spróbuj dotknąć nią kolan. ", 6,1, R.drawable.pavanamuktasana,false))
                dao.insert(Asana("Jathara Parivartanasana", "Obrócony brzuch", "Połóż się płasko na plecach na macie i rozłóż ręce na boki w linii z barkami ( ciało przypomina kształt krzyża), wnętrza dłoni skierowane są do sufitu. Z wydechem podnieś nogi do 90 stopni, skręć uda tak aby kolana były skierowane do prawej ręki, a następnie opuść nogi na ziemię, aby aby palce u nóg dotknęły prawej dłoni. Postaraj się trzymać cały czas plecy na podłodze a zwłaszcza lewy bark, łopatkę i lewą rękę.", 5,1, R.drawable.jathara_parivartanasana,false))
                dao.insert(Asana("Bitilasana", "Krowa", "Przejdź do klęku podpartego. Twoje kolana powinny być umieszczone pod biodrami, a nadgarstki w tej samej linii co ramiona. Podnieś swoje pośladki w kierunku sufitu, gdy otwierasz klatkę piersiową. Niech twoja podbrzusze kieruje się w stronę ziemi. Podnieś głowę i patrz do góry.", 2,1, R.drawable.bitilasana,false))
                dao.insert(Asana("Baddha Konasana", "Szewc", "Przygotuj sobie wałek jogiczny (ewentualnie koc złożony tak, by przypominał kształtem wałek) oraz pasek. \nUsiądź w pozycji Dandasany. Podłóż pod kość krzyżową wałek (ewentualnie koc), tak aby głowa również znajdowała się na wałku (kocu), a pośladki poza nim. Ugnij kolana, rozchyl je i połącz stopy. Przyciągnij pięty w stronę pośladków. Jeśli masz problem z przyciągnięciem stóp, możesz użyć paska, oplatając go luźno wokół pasa i zewnętrznych krawędzi stóp, dociskając dopiero w pozycji leżącej. \nJeśli jesteś gotowy, połóż się uważnie na wałku, pamiętając by pośladki pozostały poza nim. Przyciągnij pięty bliżej ciała, i dłońmi spróbuj przesunąć skórę pośladków w kierunku stóp. Ciało powinno być wyciągnięte. \nPołóż ręce poza wałkiem, wewnętrznymi dłońmi skierowanymi ku sufitowi. Pozwól ciału odprężyć się, zacznij świadomie oddychać. Oddech kieruj ku miednicy, tak by pachwiny mogły się rozluźnić a kolana zmierzać ku podłodze. Nie dociskaj ich na siłę. Jeśli czujesz duży dyskomfort może podłożyć pod kolana koce lub pianki.", 7,1, R.drawable.baddha_konasana,false))
                dao.insert(Asana("Anjaneyasana", "Wschodzący księżyc", "Rozpocznij od Adho Mukha Svanasana (Psa z Głową w Dół). Kiedy jesteś w pozycji na wydechu umieść swoją prawą stopę z przodu, między rękami. Upewnij się, że prawe kolano i kostka są w jednej linii. Delikatnie opuść lewe kolano, umieszczając je na podłodze, tuż za biodrami. \n3. Na wdechu unieś klatkę piersiową. Następnie podnieś ręce nad głową, tak aby ramiona znajdowały się obok uszu, a dłonie były skierowane ku sobie. \nWydech. Pozwól biodrom opadać i przesuwać się tak, aby poczuć przednią część nóg i zginacze bioder. \nWyjdź z pozycji kładąc dłonie na macie i pomału przechodząc do Psa z głową w dół.", 5,1, R.drawable.anjaneyasana,false))
                dao.insert(Asana("Bidalasana", "Kot", "Rozpocznij od asany czterech punktów, gdzie kolana i dłonie przylegają mocno do maty. \nUpewnij się, że Twoje kolana są umieszczone pod biodrami, a nadgarstki są w tej samej linii co ramiona. \nOpuść delikatnie głowę i spójrz w podłogę. \nOddychaj unieś kość ogonową i pchnij pępek w stronę maty. Niech Twoje podbrzusze kieruje się w stronę ziemi. Podnieś głowę i patrz do góry.", 2,1, R.drawable.bidalasana,false))
                dao.insert(Asana("Savasana", "Trup", "Savasanę wykonuj wyłącznie na koniec, nigdy w środku praktyki. Savasanę wykonuj minimalnie 5-10 minut, a jeśli jest taka potrzeba możesz ją wydłużyć nawet do 25 minut. Podczas wykonywania asan oczy pozostają otwarte i czujne. \nW przypadku pozycji nieżywego ciała wskazane jest zamknięcie oczu, by umysł i ciało mogły rozluźnić się w pełni. Podczas Savasany (szczególnie na początku) koncentruj się na głębokich i delikatnych wydechach. Dolna szczęka nie powinna być napięta, tak samo jak język i oczy. Postaraj się rozluźnić całe ciało, od stóp po czubek głowy.", 4,1, R.drawable.savasana,false))
                dao.insert(Asana("Virabhadrasana I", "Wojownik I", "Stań prosto i rozstaw nogi na szerokość bioder. Ustaw stopy tak, aby prawa noga znajdowała się z przodu, a lewa delikatni z tyłu. \nObróć prawą stopę o 90 stopni na zewnątrz, a lewą o 15 stopni. Upewnij się, że pięta prawej stopy jest wyrównana ze środkiem lewej stopy. \nUnieś swoje ręce do boku, tak aby znalazły się na wysokości ramion. \nZegnij prawe kolano, tak aby tworzyło linie prostą z kostką. Upewnij się, że kolano jest nad stopą. Lewa noga prosta. \nPołącz dłonie w Namaste i unieś je do góry. Przenieś swój wzrok na ręce. \nTrzymaj pozycję w jednej linii. Oddychaj.", 9,1, R.drawable.virabhadrasana_i,false))
                dao.insert(Asana("Tadasana", "Góra", "Stań prosto ze stopami złączonymi razem. Połącz duże paluchy stóp a pozostałe palce wyciągnij do przodu i rozszerz na boki. Dociskając środki pięt do podłogi podciągnij rzepki kolan do góry - całkowicie prostując nogi. \nRozłóż ciężar ciała równomiernie na obu stopach i od podeszw stóp wyciągnij się do góry. Wydłuż boki tułowia i podnosząc mostek do góry - rozszerz klatkę piersiową- łopatki do środka pleców. \nTrzymaj szyję i głowę prosto. Używając dłoni ściągnij barki w dół. Trzymaj całkowicie wyprostowane ręce po bokach ciała, palce dłoni wyciągnięte. \nStój prosto, z bardzo silnych, mocnych nóg wyciągnij kręgosłup i oddechem rozszerzaj klatkę piersiową. Opuszczając kość ogonową w dół podnieś kość łonową do góry- tak żeby ustawić miednicę prostopadle do podłogi.", 4,1, R.drawable.tadasana,false))
                dao.insert(Asana("Urdhva Hastasana", "Ręce podniesione do góry", "Stań w Tadasanie. Opuszczając barki w dół podnieś ręce do góry. Wnętrza dłoni skierowane do siebie. \nStopy dociśnięte do podłogi, rzepki kolan w górze. \nOpuszczając barki w dół - użyj dłoni i wyciągnij całkowicie ręce do góry. Łokcie proste. Palce dłoni do sufitu. Wnętrza dłoni równolegle do siebie.", 5,1, R.drawable.urdhva_hastasana,false))
                dao.insert(Asana("Katichakrasana", "Otwieranie boku na stojąco", "Stań w Tadasanie. Rozstaw nogi na szerokość ramion. Prawą ręką złap się za lewą łopatkę. Lewą rękę poprowadź za plecami na prawy bok. Wykonaj skręt ciała w lewą stronę.", 8,1, R.drawable.katichakrasana,false))
                dao.insert(Asana("Utthita Parsvokonasana", "Rozpostarty kąt", "Stań w tadasanie. Wdech i z wydechem skok - utthita hasta padasana - stopy pod nadgarstkami. \nSkręć lewą stopę nieznacznie do środka. Skręć prawą nogę 90 st. na zewnątrz. \nNa wdechu mostek w górę z wydechem zegnij prawą nogę do kąta prostego z kostką, udo równoległe do podłogi. Lewa noga prosta, stopa dociśnięta do podłogi. \nKolejny  wdech wyciągnij prawą rękę w bok i górę i z wydechem wyciągając prawy bok tułowia oprzyj opuszki palców dłoni na podłodze po zewnętrznej stronie stopy. Sprawdź ustawienie nóg, Lewa ręka pionowo do góry. Odpychając się od lewej pięty -wyciągnij cały lewy bok tułowia. Odpychając się palcami prawej dłoni od podłogi wyciągnij mostek do góry. Skręć klatkę piersiową. Skręć dłoń do podłogi i szerokim łukiem wyciągnij rękę za głową. \nŁopatki do środka pleców, rozszerz klatkę piersiową. Od pięty tylnej nogi do czubków palców lewej ręki maksymalne wyciągnięcie całego boku tułowia. Tylna strona ciała pozostaje w jednej płaszczyźnie. Skręt klatki piersiowej. Prawe kolano naciska na prawy łokieć, łokieć naciska na kolano. Swobodny oddech. Skręć głowę i popatrz przed lewym ramieniem na sufit.", 9,1, R.drawable.utthita_parsvokonasana,false))
                dao.insert(Asana("Virabhadrasana II", "Wojownik II", "Stań w Tadasanie. Weź głęboki wdech i z wydechem przejdź do szerokiego rozkroku, do pozycji Utthita Hasta Padasana. \nObróć prawą stopę na zewnątrz o około 90 stopni, a lewą stopę do wewnątrz o około 15 stopni. Upewnij się, że pięta prawej stopy jest idealnie wyrównana do środka lewej stopy. \nUnieś ręce do boku, tak aby znajdowały się na wysokości ramion i ułóż je równolegle do podłogi. \nWeź głęboki wdech i zegnij prawe kolano. Ważne jest, aby kolano znajdowało się w jednej linii z kostką. \nDelikatnie obróć głowę i spójrz w prawo. \nGdy już poczujesz się komfortowo, możesz zniżyć swoje biodra, wypychając miednicę w dół.", 9,1, R.drawable.virabhadrasana_ii,false))
                dao.insert(Asana("Ardha Uttanasana", "Pozycja stojąca do połowy pochylona do przodu", "Stań w Tadasanie.\nRozstaw stopy na szerokość bioder, pięty szerzej niż palce. Połóż ręce na biodrach i wydłuż boki ciała, podnieś rzepki kolan. \nZ wydechem wykonaj skłon do przodu, możesz ułóżyć ręce na oparciu krzesła/ parapecie/ drabinkach. \nWydłuż boki ciała, rozciągnij plecy. \nUłóż głowę w linii z ramionami.", 6,1, R.drawable.ardha_uttanasana,false))
                dao.insert(Asana("Adho Mukha Svanasana", "Pies z głową do dołu", "Trzymając dłonie na podłodze, zrób krok w tył. Na wydechu podwiń palce u stóp, podnieś biodra do góry i do tyłu. \nOderwij kolana od podłogi i unieś w górę. Początkowo mogą być one lekko ugięte a pięty uniesione lekko ponad podłogą. Gdy nabierzesz już doświadczenia pamiętaj, aby prostować kolana i opierać całą stopę na podłodze. \nUnieś jak najwyżej biodra. Opuść tułów i rozluźnioną głowę w stronę podłogi. Postaraj się jak najbardziej wyprostować kolana i łokcie. Unieś pośladki i wciśnij łopatki w stronę mostka. \nOddychając się rękami od podłogi wyciągnij cale ciało – poprzez barki, aż do bioder (tak jakbyś chciał się przeciągnąć). Rozluźnij szyję. Utrzymaj pozycję, oddychaj. Oddychaj przez nos.", 9,1, R.drawable.adho_mukha_svanasana,true))
                dao.insert(Asana("Catur Svanasana", "Delfin", "Uklęknij na kolanach, postaw dłonie na podłodze. Ustaw kolana bezpośrednio pod biodrami i przedramiona na podłodze, z ramionami bezpośrednio powyżej nadgarstków. Łokcie powinny być ułożone na szerokość ramion. Mocno przyciśnij dłonie i przedramiona do podłogi. \nPostaw palce stóp na podłodze, na wydechu unieś z podłogi kolana. Na początku pozostaw kolana lekko ugięte a pięty uniesione w górę. Staraj się wydłużać kość ogonową odciągać ją odmiernicy i dociskać nieznacznie w stronę kości łonowej. W przeciwstawnym kierunku unoś kości pośladków, czyli w stronę sufitu. Zaczynając ruch od wewnętrznych kostek wyciągaj wewnętrzne części nóg w stronę pachwin. \nW dalszym ciągu dociskaj aktywnie przedramiona do podłogi. Możesz połączyć dłonie. Unoś żebra i rozszerzaj je kierując ruch w stronę kości ogonowej. \nTrzymaj głowę pomiędzy ramionami. Nie pozwól żeby zwisała ani też nie dociskaj jej zbyt mocno do podłogi, powinna delikatnie opierać się o matę. \nJeżeli nie odczuwasz bólu w plecach, wyprostuj kolana. Kontynuuj ruch wydłużania kości ogonowej, w celu oddalania jej od miednicy. Oddalaj górną część mostku od podłogi.", 9,1, R.drawable.catur_svanasana,false))
                dao.insert(Asana("Uttana Shishosana", "Rozciągający się szczeniaczek", "Uklęknij na podłodze, połóż dłonie przed sobą. Upewnij się, że barki są powyżej nadgarstków a biodra dokładnie nad kolanami. \nPowędruj dłońmi o kilka centymetrów do przodu. \nNa wydechu przesuń nieznacznie pośladki do tyłu, w stronę pięt. \nUtrzymaj aktywne ręce, nie pozwól łokciom opaść na podłogę. \nOprzyj delikatnie głowę na podłodze, rozluźnij szyję. Dolne plecy powinny tworzyć lekki łuk. \nAby poczuć miłe wydłużanie kręgosłupa, dociskaj dłonie do podłogi i wyciągaj się od ramion, wypychając lekko biodra w stronę piet. Poczuj jak twoje ciało rozciąga się i wydłuża w dwóch kierunkach.", 6,1, R.drawable.uttana_shishosana,false))
                dao.insert(Asana("Makarasana", "Krokodyl", "Ułóż się w leżeniu na brzuchu. Stopy rozstaw na szerokość bloder, uda zrotuj na zewnątrz tak, aby ich przyśrodkowa strona ułożona była na macie. Stopy ułóż grzbietami na macie. Ręce wyprostuj, a następnie zegnij w łokciach, prawą dłoń ułóż na lewym ramieniu lub przedramieniu, lewą dłoń ułóż na prawym ramieniu lub przedramieniu. Brodę ułóż na skrzyżowaniu rąk. ", 2,1, R.drawable.makarasana,false))
                dao.insert(Asana("Dandasana", "Siad prosty", "Usiądź na na takiej wysokości, żeby miednica była ustawiona prostopadle do podłogi plecy pionowo wyprostowane. \nPomóż sobie odciągając mięśnie pośladków do tyłu i w bok wyprostuj nogi, złącz uda, kolana, kostki i duże paluchy. \nWciskaj tyły nóg w podłogę. \nWyciągnij palce stóp do sufitu. Połóż końce palców dłoni za pośladkami na kocu, palce skierowane do przodu. \nRęce zgięte w łokciach tak żeby odpychając się palcami od koca wyciągnąć się do góry. \nMostek do góry, barki w dół. \nOdpychając się dłońmi od koca , wyciągnij kręgosłup w górę. Zrób całkowicie proste plecy. Łopatki zassane w głąb pleców. \nGłowa prosto, czubek głowy do sufitu. wzrok przed siebie. Oddychaj normalnie, twarz rozluźniona.", 9,1, R.drawable.dandasana,false))
                dao.insert(Asana("Balasana", "Dziecko", "Usiądź na swoich piętach i połącz duże palce u stóp. Kiedy poczujesz się komfortowo, rozsuń kolana na szerokość swoich bioder. \nWydech. Pochyl się do przodu i połóż swój tors pomiędzy udami. \nPołóż czoło na podłodze. Rozszerz kość krzyżową na szerokość miednicy, a kolce biodrowe skieruj do swojego pępka. Wyciągnij pośladki w stronę pięt, wydłużając przy tym kręgosłup. \nWyciągnij ręce przed siebie tak, aby były równo z kolanami", 6,1, R.drawable.balasanaa,false))
                dao.insert(Asana("Sukhasana", "Siad po turecku", "Usiądź płasko na ziemi z połączonymi, wyprostowanymi nogami nogami – Dandasana. \nZegnij nogi i skrzyżuj je, stopy wysuń nieco do przodu i wsuń jak najbardziej pod kolana, równocześnie oddalając je od siebie. \nUda powinny być ustawione równolegle do podłogi. \nGrzbiety rąk umieść na kolanach. Połącz końce palców wskazujących i kciuków (jnana-mudra). \nWyciągnij do góry tułów i kręgosłup, otwórz klatkę piersiową podnosząc mostek do góry, rozluźnij brzuch i podbrzusze.", 7,1, R.drawable.sukhasana,false))
                dao.insert(Asana("Parivritta Janu Sirsasana", "Głowa obrócona do kolan", "Usiądź w Dandasanie, z prostym kręgosłupem i wydłużonymi nogami wyciągniętymi do przodu. Zegnij prawe kolano, przyciągając piętę do prawej pachwiny. Lewą nogę mocno wciśnij w podłogę, z pietą prostopadłą do podłogi. Ustaw równo oba biodra. \nPodnieś prawe ramię na bok i skręć je do stawu barkowego, taka by wnętrze dłoni zwrócone było do tyłu Odkręć tułów w prawo, w stronę ugiętej nogi, zaczynając od dołu brzucha a na barkach kończąc. Umieść prawa rękę z tyłu za plecami i opleć ją za nimi, tak aby prawą dłonią sięgnąć prawego biodra, umieść dłoń na udzie. \nWyciągnij lewe ramię do góry, pozwól by ręka wyciągnęła tułów do góry, a następnie skieruj lewą rękę do lewej wyciągniętej nogi. Chwyć dłonią za piętę. Staraj się utrzymać wyciągnięty tułów równolegle do wyprostowanej nogi. Przesuń prawy bark do góry i do tyłu, co pozwoli otworzyć klatkę piersiową. \nPrzyciągnij lewy łokieć do podłogi. Wygnij żebra lewego boku do środka, a prawego na zewnątrz, tak aby kręgosłup przybrał kształt litery C. \nPalce lewej dłoni, oplatając lewą stopę powinny być zwrócone do góry. Zdejmij prawą dłoń z biodra i unieś ją do góry a następnie przenieś w kierunku lewej nogi. Dłońmi prawej ręki obejmij zewnętrzną część lewej stopy. Jeżeli nie możesz sięgnąć stopy - użyj paska. Przesuń do tyłu prawy bok, tak aby znalazł się bezpośrednio nad lewym barkiem. Zegnij jeszcze mocniej lewy łokieć, tak aby lewy bark przybliżyć bardziej do podłogi. Rozciągaj zewnętrzny prawy bok tułowia. Spójrz ku górze, pod górnym ramieniem.", 8,1, R.drawable.parivritta_janu_sirsasana,false))
                dao.insert(Asana("Padma Matsyendrasana", "Władca ryb II", "Usiądź w Dandasanie. Prawą nogę zegnij w kolanie i przełóż za lewą nogę. Lewą rękę zegnij w łokciu i zahacz o kolano prawej nogi, tak żeby wykonać skręt kręgosłupa. Prawą rękę postaw za plecami i postaraj się jeszcze bardziej rozciągnąć.", 8,1, R.drawable.padma_matsyendrasana_ii,false))
                dao.insert(Asana("Viparita Karani", "Nogi uniesione na ścianie", "W tej pozycji zawsze używamy wałka lub zwiniętego koca (uwaga: jeśli kręgosłup nie jest elastyczny, wałek powinien być cieńszy, w miarę praktyki możemy owijać go kocem, aby zwiększyć jego objętość). \nPołóż się przy ścianie na prawym boku i zegnij nogi w kolanach, przyciągnij je do klatki piersiowej. Przestrzeń między ścianą a pośladkami powinna wynosić około 15 cm. \nPodczas wydechu, jednym ruchem wyprostuj nogi do góry, opierając je o ścianę. \nPośladki nie dotykają ściany, lecz wraz z kością ogonową są rozluźnione do dołu. \nKlatka piersiowa jest rozluźniona, lecz ze względu na sposób w jaki opiera się kręgosłup na wałku, będzie lekko wysunięta do góry. \nMusisz znaleźć pozycję, w której poczujesz, że zarówno pośladki jak i górna część kręgosłupa są rozluźnione do dołu. \nRozluźnij mięśnie twarzy, lekko otwierając usta, język leży w dolnej części jamy ustnej. \nRozluźnij krtań, przesuń głowę lekko do tyłu, tak, aby nie napinały się mięśnie karku. \nW tej pozycji ważne jest, aby nasze zmysły mogły się wyciszyć; zamknij więc oczy i jeśli jest to możliwe, włącz cichą, relaksującą muzykę lub pozostań w ciszy.", 9,1, R.drawable.viparita_karani,false))
                dao.insert(Asana("Mula Bandha", "Blokada korzenia", "Brak opisu", 7,2, R.drawable.mula_bandha,false))
                dao.insert(Asana("Kapalabhati", "Lśniący oddech czaszki", "Brak opisu", 7,2, R.drawable.kapalabhati,false))
                dao.insert(Asana("Jalandhara Bandha", "Wiąz, który podtrzymuje nektar", "Brak opisu", 7,2, R.drawable.jalandhara_bandha,false))
                dao.insert(Asana("Salamba Bhujangasana", "Sfinks", "Połóż się płasko na brzuchu. Połóż ręce wzdłuż ciała. \nNastępnie przesuń dłonie do przodu, upewniając się, że znajdują się na wysokości ramion i połóż dłonie na podłodze. \nTeraz, kładąc ciężar ciała na dłoniach, weź wdech i podnieś głowę do góry. Zwróć uwagę, aby Twoje ręce były ugięte w łokciach. \nPamiętaj, aby spiąć łopatki i oddalić ramiona od uszu. \nWciśnij biodra, uda i stopy mocno w podłogę", 2,2, R.drawable.salamba_bhujangasana,false))
                dao.insert(Asana("Ardha Kapotasana", "Półgołąb", "Pozycją wyjściowa jest pies z głową w dół. \nPrzenieś lewe kolano do przodu tak, aby piszczel miał styczność z podłożem, a stopa z prawym nadgarstkiem (jeśli jesteś początkujący umieść stopę z przodu przy przeciwnym biodrze). \nDruga noga wyprostowana w tyle w jednej linii z biodrem. \nOpuść biodra do podłoża. Żeby przejść do półgołębia należy unieść ręce do góry i wygiąć kręgosłup lekko do tyłu. Żeby przejść do pełnego gołębia przenieś ręce do przodu i położysz tułów na podłodze (jeśli jesteś początkujący wystarczy, że umieścisz na podłodze przedramiona).", 7,2, R.drawable.ardha_kapotasana,false))
                dao.insert(Asana("Chaturanga Dandasana", "Niska deska", "Przejdź do pozycji deski, upewniając się, że zewnętrzna krawędź twoich ramion znajduje się w tej samej linii, co środkowe palce na podłodze. \nRozłóż szerzej swoje palce i rąk i przyciśnij dłonie mocno do maty. \nWydech. Delikatnie opuść ciało, tak aby ramiona znajdowały się równolegle do podłogi. Podczas opuszczania, czubki łokci muszą lekko dotykać boków żeber, aby utrzymać kąt 90 stopni.", 9,2, R.drawable.chaturanga_dandasana,false))
                dao.insert(Asana("Parsva Balasana", "Boczne dziecko", "Przejdź do pozycji dziecka. Unieś się na przedramiona. Prawą dłoń poprowadź wewnętrzną stroną do góry po macie na lewą stronę. Oprzyj prawy  policzek na macie. Wyciągnij lewą ręką za głowę i wyprostuj ją. Rozluźnij.", 8,2, R.drawable.parsva_balasana,false))
                dao.insert(Asana("Ardha Chandrasana", "Półksiężyc", "Rozpocznij od pozycji rozciągniętego trójkąta (Utthita Trikonasana) zaczynając od prawego boku. \nPołóż lewą dłoń na biodrze. Na wdechu zegnij lekko kolano i połóż opuszki palców prawej dłoni na podłodze lub na tak aby znajdowały się w odległości ok.30-50cm stopy. \nOderwij lewą stopę na od podłogi i przysuń ją do prawej, tak aby ciężar ciała spoczywał na ugiętej prawej nodze. Staraj się oddychać spokojnie by utrzymać równowagę. \nDociśnij prawą stopę do podłogi. Na wydechu wyprostuj prawą nogę, tym samym lewą nogę unieś do góry. Lewa noga powinna być równoległa do podłogi, lub trochę powyżej. Utrzymaj uniesioną nogę silną i mocno wyciągniętą. Pilnuj aby rzepka kolanowa nie przekręcała się. \nZrotuj górną część tułowia w lewą stronę. Klatka piersiowa powinna być odwrócona przodem do ściany. Wydłużaj kość ogonową w stronę uniesionej stopy. \nStaraj się utrzymać równowagę, kiedy czujesz się stabilnie, wyciągnij lewą rękę do góry, tak aby była przedłużeniem prawej ręki. Spójrz do góry w stronę wyciągniętej w górę dłoni. ", 3,2, R.drawable.ardha_chandrasana,false))
                dao.insert(Asana("Vrksasana", "Drzewo", "Stań w pozycji Tadasana i opuść ręce wzdłuż tułowia. \nLekko ugnij prawe kolano i połóż prawą stopę wysoko na lewym udzie. Postaraj się, aby stopa była jak najbliżej pachwiny. \nLewa noga musi być wyprostowana. Złap równowagę i oddychaj. \nUnieś ręce nad głowę i ułóż je w “namaste”. \nZnajdź punkt w oddali i skup się na nim. Pomoże Ci to utrzymać równowagę. \nTrzymaj kręgosłup w jednej linii i postaraj się zrelaksować.", 3,2, R.drawable.vrksasana,false))
                dao.insert(Asana("Uttanasana", "Głęboki skłon", "Stań w Tadasanie z dłońmi na biodrach. Rozstaw nogi na szerokość bioder. Napnij mięśnie ud, unieś rzepki kolanowe do góry. \nNa wdechu unieś do góry ramiona. \nNa wydechu zegnij tułów prowadząc ruch od bioder. W tej pozycji tułów powinien być równoległy do podłogi. Dłonie oprzyj na biodrach a następnie opuść je ku ziemi. Postaw na podłodze koniuszki palców. Spojrzenie skieruj w dół, nie napinaj mięśni szyi. Uda zrotuj do wewnątrz. \nOddychaj spokojnie i głęboko. Wyciągaj tułów do przodu, zachowaj proste, niemal wklęsłe plecy. Pół-pozycja jest bardzo korzystna dla osób skarżących się na dyskopatię lędźwiową. Ciężar ciała powinien znajdować się na przedniej części stóp. Staraj się wyciągać równo oba boki zachowując płaski brzuch. \nDociśnij całe dłonie do podłogi a następnie po kilku głębokich oddechach połóż dłonie tuż obok stóp i przyciągnij tułów bliżej nóg. Jeżeli nie możesz sięgnąć podłogi, ugnij lekko kolana. Jeżeli ta pozycja sprawia ci trudność, zapleć ręce trzymając się za łokcie i ciągnij tułów w stronę podłogi. \nJeżeli możesz położyć dłonie tuż obok kostek i wyprostować kolana, spróbuj wykonać pełną wersję pozycji. Na każdym wdechu wyciągaj tułów do przodu a na wydechu opuszczaj go w dół. Dociskaj pięty do podłogi, kości pośladkowe kieruj w stronę sufitu. Głowa powinna wisieć luźno. Przyciągaj tułów do kolan, jeżeli możesz, przyciągnij na końcu również głowę. Pogłębiaj pozycję dopóki czujesz że twoje plecy są proste.", 6,2, R.drawable.uttanasana,false))
                dao.insert(Asana("Utkatasana", "Krzesło", "Stań w Tadasanie, stopy równolegle, palce i pięty razem. \nUgnij lekko kolana, stopniowo schodząc do przysiadu. Zejdź w dół do pozycji, w której Twoje uda i golenie będą pod kątem 90 stopni. Pięty na podłodze. \nOpuść górne pośladki do podłogi, by nie zaciskać dolnego kręgosłupa. \nWyciągnij ręce nad głową. Połącz dłonie nie zapadając się w mostku. \nTułów wydłużony, zassij pępek do kręgosłupa. \nGłowa lekko wzniesiona, spoglądaj w kierunku uniesionych dłoni. ", 9,2, R.drawable.utkatasana,false))
                dao.insert(Asana("Padmasana", "Lotos", "Usiądź na macie w pozycji Dandasany (kija na siedząco). Następnie zegnij prawą nogę w kolanie. Chwyć za dłońmi za stopę i przyciągnij kończynę do siebie. Teraz połóż prawą stopę na wierzchu lewego uda, które na razie powinno pozostać wyprostowane. Kiedy twoja pięta znajdzie się blisko pępka (możesz poprawiać jej ułożenie do odpowiedniego skutku) przyjmiesz pozycję jogi zwaną pół-lotosem. \nKiedy już stabilnie siedzisz w pół-lotosie, zegnij lewą nogę i chwyć za jej kostkę obiema rękami i przyciągnij nogę do brzucha. Połóż stopę u nasady uda tak, jak zrobiłeś to z prawą nogą. Postaraj się w miarę możliwości zbliżyć do siebie kolana, ale pamiętaj, aby wciąż spoczywały one na podłodze. \nTeraz postaraj się jak najbardziej wyprostować kręgosłup. Następnie połóż swoje dłonie na kolanach. Wnętrza dłoni muszą być skierowane do góry. Ściągnij łopatki i popraw ustawienie kręgosłupa. Teraz wykonaj złączenie kciuków i palców wskazujących, które powinny tworzyć ze sobą kształt koła.", 7,2, R.drawable.padmasana,false))
                dao.insert(Asana("Paschimottanasana", "Rozciąganie do przodu w siadzie", "Usiądź na podłodze z pośladkami opartymi na złożonym kocu i nogami wyprostowanymi przez tułowiem (Dandasana) z dłońmi na podłodze tuż obok bioder. Dociskaj aktywnie pięty i tyły ud do podłogi, palce stóp powinny być skierowane do sufitu. Wewnętrzne pachwiny chowają się w głąb miednicy. Przesuń miednicę do przodu, aby ciężar ciała znajdował się z przodu pośladków. Wciśnij pośladki mocno w podłogę, następnie na wdechu wyciągnij wyprostowane ręce ponad głowę. Podnieś mostek i wyciągnij kręgosłup w górę, w kierunku czubka głowy. \nPrzy wydechu łagodnie przesuń łono w kierunku kręgosłupa i ugnij do przodu górną część ciała. Wyciągnij ręce wzdłuż nóg, próbując sięgnąć dużych palców nóg, lub boków stóp a następnie uchwycić je palcami dłoni, lub objąć przy pomocy nadgarstka. Łokcie powinny być proste, w pełni wyciągnięte. Jeżeli nie możesz sięgnąć stóp, użyj paska, lub nie prostuj kolan. \nWysuń do przodu klatkę piersiową, utrzymaj proste plecy. Patrz do przodu, przy wdechu unieś mostek, przesuń żebra z dala od bioder, w kierunku kolan. Ruch do przodu powinien wychodzić z łona i kręgosłupa lędźwiowego, a nie z głowy i barków. \nJeżeli bez problemu sięgasz stóp, zegnij łokcie i na wydechu pogłęb pozycję, przywodząc czoło do kolan. Brzuch powinien leżeć na udach jako pierwszy, następnie żebra a dopiero potem głowa. Jeśli twoje ciało nie jest na tyle elastyczne, nie opuszczaj głowy, ale trzymaj kręgosłup szyjny w jednej linii z resztą kręgosłupa. Przesuń barki dalej od uszu. \nZ każdym wdechem nieco unoś i wydłużaj tułów, a przy wydechu zbliżaj tułów do kolan.", 6,2, R.drawable.paschimottanasana,false))
                dao.insert(Asana("Ardha Padmasana", "Półlotos", "Usiądź płasko na ziemi z nogami wyprostowanymi i połączonymi – Dandasana. \nZegnij i skrzyżuj nogi do Svastikasany najpierw zginając lewą, a następnie prawą nogę. Wysuń prawą stopę spod lewego kolana i pozwól aby lewe kolano opadło na ziemię prawe kolano powinno opaść na lewą stopę. \nJeśli kolana nie opadają, pozostawaj na tym etapie, rozluźniając biodra, do momentu aż kolana opadną. Gdy kolana opadną, chwyć prawą stopę i wsuń ją na szczyt lewego uda. \nPołóż grzbiety rąk na udach,wyciągnij do góry tułów i kręgosłup. \nOtwórz klatkę piersiową podnosząc mostek do góry, rozluźnij brzuch i podbrzusze.", 7,2, R.drawable.ardha_padmasana,false))
                dao.insert(Asana("Vajrasana", "Błyskawica", "Usiądź płasko na ziemi z nogami wyprostowanymi i połączonymi. \nzginając nogi przełóż je bokiem i usiądź na stopach. \nStopy wyciągnięte do tyłu a pięty dokładnie pod guzami kulszowymi (kośćmi pośladków). \nRozluźnij nogi całkowicie, miednica ustawiona pionowo a dolne plecy lekko wklęsłe. \nOtwórz klatkę piersiową podnosząc mostek cofając barki. \nTwarz ustaw pionowo i wyciągnij czubek głowy, grzbiety rąk połóż na udach blisko bioder utrzymując proste plecy rozluźnij się całkowicie.", 4,2, R.drawable.vajrasana,false))
                dao.insert(Asana("Janu Sirsasana", "Pozycja kolana i głowy uniesionych do góry", "Usiądź w Dandasanie na takiej wysokości żeby plecy były wyprostowane. \nZegnij prawą nogę i umieść piętę blisko krocza, kolano na podłodze. Podeszwa prawej stopy dotyka wewnętrznej strony lewego uda. Lewa noga wyprostowana - naciśnij piętą na podłogę. \nWyprostuj plecy i naciskając prawą dłonią na udo lewej nogi skręć tułów tak, żeby mostek był skierowany dokładnie do stopy lewej nogi. \nLewa stopa ustawiona pionowo do sufitu, pięta wypchnięta, palce ściągnij do siebie rozciągając skórę na podeszwie stopy. \nNa wdechu wyciągnij ręce pionowo do góry, dociśnij lewą piętę do podłogi i z wydechem wyciągnij tułów do przodu- zachowując otwarcie w klatce piersiowej. Złap dłońmi za stopę. \nZassij łopatki do środka pleców i wyciągnij mostek mocno w górę, wyprostuj plecy", 6,2, R.drawable.janu_sirsasana,false))
                dao.insert(Asana("Ardha Matsyendrasana", "Półlkról ryb", "Usiądź w Dandasanie, możesz uprzednio położyć na macie złożony koc. Zegnij oba kolana i umieść stopy na podłodze. \nOprzyj lewe udo na podłodze, przeciągając lewą stopę pod prawą nogą, tak aby lewa pięta znajdowała się tuż przy prawym biodrze. \nPrzełóż prawą stopę ponad lewą nogą, postaw ją na podłodze tuż przy krawędzi lewego uda lub jeżeli możesz obok lewego biodra. Prawe kolano powinno być skierowane w stronę sufitu, podczas gdy lewe jest przyciśnięte do podłogi. \nNa wydechu skręć tułów w stronę prawego uda. Prawa dłoń powinna znajdować się tuż za prawym pośladkiem, dociskaj ją mocno do podłogi i niejako od niej odpychaj się do skrętu. Umieść lewe przedramię po zewnętrznej stronie prawego uda w pobliżu kolana. Skieruj rozpostartą dłoń w stronę sufitu. Staraj się przyciągać górną część tułowia do wewnętrznej strony prawego uda. \nAktywnie dociskaj wewnętrzną część prawej stopy do podłogi. Wyciągaj tułów do góry, zachowując proste plecy. Ściągaj łopatki do środka, a kość ogonową kieruj do podłogi. \nSkieruj głowę w przeciwną stronę niż wyciągnięta w górę dłoń, w tym przypadku w prawo. Na każdym wdechu unoś do góry mostek, odpychając się od dłoni umieszczonej na podłodze. Na każdym wydechu skręcaj się coraz mocniej.", 8,2, R.drawable.ardha_matsyendrasana,false))
                dao.insert(Asana("Ardha Navasana", "Półokręt", "Usiądź w Dandasanie. \nPołóż swoje dłonie na podłodze, ugnij delikatnie łokcie. Wyprostuj plecy, wyciągnij kręgosłup, upewnij się, że się nie garbisz. \nOddychaj. Zegnij nogi w kolanach i unieś je aż do momentu, gdy łydki znajdą się równolegle do podłogi. Podbródek skieruj delikatnie w stronę mostka. Trzymaj pozycję mięśniami brzucha. \nOddychaj. Wyprostuj ręce przed siebie, upewnij się, aby palce dłoni były na wysokości Twojego wzroku. Napnij mocno mięśnie brzucha.", 9,2, R.drawable.ardha_navasana,false))
                dao.insert(Asana("Simha Vajrasana", "Lew", "Usiądź w klęku, pochyl się lekko do przodu podnosząc pośladki, a następnie skrzyżuj kostki u nóg. Trzymaj lewą stopę bliżej podłogi. Palce stóp staraj się skierować do tyłu. \nTeraz możesz opuścić kość ogonową w stronę podłogi i wyciągnąć w górę kręgosłup (jeżeli jest Ci niewygodnie, opcjonalnie pod stopy połóż zwinięty koc lub przejdź do siadu klęcznego). \nDłonie umieść na kolanach i skieruj je wnętrzem do dołu. Rozłóż szeroko palce, wyprostuj ręce i napnij mięśnie: od barków po końce palców. \nZamknij oczy i weź głęboki wdech. Na wydechu pochyl się do przodu i otwórz szeroko usta wysuwając język jak najdalej przed siebie, kierując go w dół w stronę brody.", 7,2, R.drawable.simha_vajrasana,false))
                dao.insert(Asana("Marichyasana I", "Mędrzec I", "Usiądź w pozycji Kija na siedząco (Dandasana). Zegnij lewe kolano przyciągnij lewą stopę blisko do pośladka i umieść ją tuż przy wewnętrznej krawędzi prawego uda ( w odległości ok. 5 cm. Prawa noga pozostaje mocno wciśnięta w podłogę, palce stóp skierowane w stronę sufitu. \nOprzyj prawą dłoń na podłodze tuż za prawym biodrem. Powoli unoś miednicę, wyciągnij lewe ramię w stronę wyprostowanej nogi, tak aby pochylony tułów znalazł się pomiędzy nogami. Wewnętrzne udo powinno dociskać do bocznej części tułowia. Sięgnij lewą dłonią lewej stopy, lewy bark zwróć do wewnątrz. Po kilku oddechach powoli zwróć tułów prosto (równolegle do podłogi) w stronę wyprostowanej nogi. \nPrzenieś lewą rękę za siebie, tak aby ramię owijało się wokół lewego kolana. Teraz wyciągnij do przodu prawe ramię, obróć bark i na wydechu sięgnij prawą ręką za siebie, tak aby prawą dłonią uchwycić lewy nadgarstek. Jeżeli nie możesz połączyć dłoni - użyj paska. \nNa wydechu wyciągaj tułów do przodu, ruch powinien zaczynać się od miednicy, staraj się położyć na prawej nodze: wydłużony brzuch i klatkę piersiową. Pamiętaj że plecy nie mogą być zgarbione, pochylaj się do momentu kiedy czujesz, że twoje plecy są wciąż proste. \nCzubek głowy wyciągnij w kierunku palców stóp a następnie przysuń podbródek do prawej goleni, nie napinaj mięśni szyi. Utrzymaj mocną prawą nogę, kolano i stopa muszą być skierowane ku górze, nie przekręcać ich na bok. Oddalaj ramiona od uszu, odciągaj nadgarstki do tyłu, oddalając je od pleców.", 6,2, R.drawable.marichyasana_i,false))
                dao.insert(Asana("Agni Stambhasana", "Kostki do kolan", "Usiądź w Sukhasanie Unieś rękami prawą nogę i umieść ją na lewej, tak aby koniec stopy znalazł się w jednej linii z kolanem. ", 7,2, R.drawable.agni_stambhasana,false))
                dao.insert(Asana("Supta Vajrasana", "Leżący bohater", "Usiądź w Virasanie. \nPodnosząc biodra, podwijając kość ogonową oprzyj się na przedramionach. \nPołóż plecy na ziemi. Złap rękami za swoje pięty.\nDociskaj kolana do podłogi.", 2,2, R.drawable.supta_vajrasana,false))
                dao.insert(Asana("Vasisthasana", "Boczna deska", "Zacznij w pozycji deski – połóż się na brzuchu, po czym unieś górną część ciała, podpierając się na dłoniach (dłonie pod barkami, łokcie wyprostowane) lub przedramionach i utrzymując ciało w linii prostej. \nNastępnie obróć całe ciało o 90 stopni, podpierając się na jednej dłoni lub przedramieniu, i prostując wolne ramię ku górze. \nTrzymaj stopy jedna na drugiej lub obok siebie równolegle do podłogi.", 1,2, R.drawable.vasisthasana,false))
                dao.insert(Asana("Hanumanasana", "Małpa", "Do pozycji wchodzimy z pozycji psa z głową w dół przesuwając prawą nogę po macie do przodu. Powoli prostuj lewą i prawą nogę starając się dociskać prawe udo do podłogi. Palce tylnej nogi są podwinięte, co pomaga utrzymać miednicę w pionie. Jeśli dasz radę, możesz wyprostować palce tylnej nogi i wyciągnąć stopę. \nNależy rozłożyć wagę równomiernie na obie nogi, aby zapobiec przechylaniu się miednicy. Upewnij się, że centrum rzepki kolanowej skierowane jest do góry. \nNogi aktywne. Udo nogi z przodu zrotowane do wewnątrz. Kolano nogi tylnej dociśnięte do maty. Pamiętaj, aby tył stawu kolanowego nogi znajdującej się z przodu i kolano nogi z tyłu dotykały podłogi. Tył prawej nogi i przód lewej nogi powinny dotykać ziemi. nały czas mocno dociskaj przednie udo. \nWessij brzuch i unieś klatkę piersiową.\nUnieś się lekko, by zrobić przestrzeń między podłogą a biodrami i ponownie dociśnij miednicę do podłogi, ustaw równo guzy kulszowe, unieś ręce do góry.", 7,3, R.drawable.hanumanasana,false))
                dao.insert(Asana("Vrschikasana", "Skorpion", "Klęknij na macie w klęku podpartym. Ustaw łokcie, przedramiona i dłonie równolegle na ziemi. (przedramiona powinny być rozstawione na szerokość barków). \nWyciągnij szyję i podnieś głowę do góry tak wysoko, jak tylko możesz. \nWykonaj wdech, podnieś do góry nogi i tułów. Utrzymuj równowagę, nie pozwalając nogom opaść za głowę. Wyciągnij okolicę piersi pionowo do góry i utrzymuj równowagę. Jest to pinca-mayurasana. \nPo okresie balansowania na przedramionach wykonaj wydech, zegnij kolana, podnieś szyję i głowę do góry, najwyżej jak potrafisz, odciągnij kręgosłup od barków, opuść stopy i staraj się dotknąć piętami czubka głowy. (od pięt po kolana nogi powinny być w linii pionowej z głową, a golenie i ramiona wzajemnie równoległe). \nPonieważ szyja, barki, klatka piersiowa, kręgosłup i brzuch są w tej pozycji napięte, oddech będzie bardzo szybki i ciężki. Staraj się natomiast oddychać normalnie i pozostań w pozycji kilka oddechów, stosownie do możliwości.", 1,3, R.drawable.vrschikasana,false))
                dao.insert(Asana("Malasana", "Girlanda", "Kucnij na ziemi i połącz stopy razem. Ważne, żeby stopy były całe na ziemi. Jeżeli nie dasz rady to podłóż pod nie matę albo koc. \nRozszerz swoje uda. Powinny być szersze niż klatka piersiowa. \nOddychaj i delikatnie przesuń swój tułów tak, żeby znajdował się między udami. \nPołącz dłonie ze sobą i oprzyj łokcie do wewnętrznych ud. Dzięki temu wydłużysz swoje plecy. \nWydłuż kręgosłup i szyję.", 7,3, R.drawable.malasanaa,false))
                dao.insert(Asana("Anantasana", "Kanapa", "Połóż się na boku. Podeprzyj głowę na ręce zgiętej pod kątem 90 stopni. Drugą dłonią złap za duży palec u nogi, która nie przylega bezpośrednio do maty. Następnie spróbuj trzymając za paluch wyprostować nogę przyciągając ją do góry.", 9,3, R.drawable.anantasana,false))
                dao.insert(Asana("Urdhva Mukha Svanasana", "Pies z głową do góry", "Połóż się na brzuchu. Na wydechu obciągnij palce u stóp i podnieś biodra do góry. \nRozprostuj nogi, wierzchołki stóp oparte na podłodze. Stopy na szerokości bioder. \nZgięte łokcie przy ciele. Rozłóż dłonie na macie obok pasa, tak aby przedramiona były prostopadle do podłogi.\nNa wydechu wyprostuj ręce i jednocześnie unieś tułów. Nogi kilka centymetrów nad podłogą. \nNogi muszą być wyprostowane i napięte w kolanach, a ramiona ściągnięte w dół i oddalone od uszu. Cały ciężar ciała powinien spoczywać na dłoniach oraz wierzchniej stronie stóp.", 2,3, R.drawable.urdhva_mukha_svanasana,false))
                dao.insert(Asana("Dhanurasana", "Łuk", "Połóż się na macie brzuchem do dołu. Zegnij nogi. Złap się za stopy. Weź wdech. Trzymając się za stopy, podnieś się maksymalnie z maty. Cały czas wstrzymuj oddech.", 2,3, R.drawable.dhanurasana,false))
                dao.insert(Asana("Ardha Dhanurasana", "Półłuk", "Połóż się na macie brzuchem do dołu. Zegnij prawą nogę. Złap się za stopę tej nogi prawą dłonią. Weź wdech. Trzymając się za stopę, podnieś się maksymalnie z maty. Cały czas wstrzymuj oddech.", 2,3, R.drawable.ardha_dhanurasana,false))
                dao.insert(Asana("Setu Bandhasana", "Most", "Połóż się na macie na plecach, zegnij nogi w kolanach, podnieś biodra do góry. Spleć palce rąk za plecami podwiń barki pod siebie, tak żeby stanąć na szczytach barków. \nNogi zgięte w kolanachmklatka piersiowa bardzo mocno otwarta, brzuch kompletnie rozluźniony. \nJeżeli chcesz przejść do pełnego wariantu pozycji - wyprostuj nogi, zaktywizuj stopy, wypchnij pięty. Stopy mogą być ustawione na szerokość bioder.", 2,3, R.drawable.setu_bandhasana,false))
                dao.insert(Asana("Eka Pada Setuasana", "Jednonogi mostek", "Połóż się na macie na plecach, zegnij nogi w kolanach, podnieś biodra do góry. Spleć palce rąk za plecami podwiń barki pod siebie, tak żeby stanąć na szczytach barków. \nNogi zgięte w kolanachmklatka piersiowa bardzo mocno otwarta, brzuch kompletnie rozluźniony. \nPodnieś jedną z nóg góry i postaraj się wyprostować ją w kolanie. Uniesiona noga powinna być podniesiona pod kątem 90 stopi względem podłogi. ", 2,3, R.drawable.eka_pada_setuasana,false))
                dao.insert(Asana("Dvapada Dhanurasana", "Most na łokciach", "Połóż się na plecach, stopy rozstawione nieco szerzej niż na szerokość bioder. \nZegnij ręce i połóż dłonie obok uszu. \nUnieś biodra, ramiona i głowę z podłogi, prostując ręce. \nZegnij ręce i połóż czubek głowy na macie. \nPołóż przedramiona na macie i złap się dłońmi za głowę. \nTrzymaj otwartą klatkę piersiową", 2,3, R.drawable.dvapada_dhanurasana,false))
                dao.insert(Asana("Salamba Sarvangasana", "Świeca", "Połóż się na plecach. Ręce ułóż wzdłuż tułowia a dłonie wnętrzami do podłogi. Z wydechem przyciągnij zgięte kolana do tułowia, mocno dociśnij i odepchnij się rękami od podłogi i jednym ruchem unieś biodra. Ugnij łokcie i podeprzyj dolne plecy rękami. Zbliż do siebie oba łokcie tak, by ramiona były równoległe względem siebie. Pozostań na 1 oddech. \nPodczas wydechu ustaw tułów do pionu podpierając się rękami- tak by broda dotknęła klatki piersiowej i wyprostuj nogi. Palce stóp skieruj do sufitu. Utrzymuj gardło rozluźnione, tak aby nie blokowało swobodnego oddechu. Pozostań na 5 oddechów. \nZ kolejnym wydechem ugnij nogi i powoli opuść tułów na podłogę, podpierając plecy rękami. Rozprostuj nogi i ręce, odpocznij.", 3,3, R.drawable.salamba_sarvangasana,false))
                dao.insert(Asana("Udhva Prasarita Padasana", "Uniesione nogi", "Połóż się na plecach. Wyciągnij ręce za głową, zapleć kciuki. \nZegnij nogi w kolanach i wyprostuj je do 90 stopni w górę. \nWyciągnij pięty, skręć uda do środka, mocno wydłużaj rękami boki tułowia.", 9,3, R.drawable.urdhva_prasarita_padasana,false))
                dao.insert(Asana("Supta Padangusthasana", "Leżąca od ręki do dużego palucha", "Połóż się na plecach. Wyprostuj kompletnie nogi. \nPrzygotuj dużą pętlę z paska. \nZegnij lewą nogę w kolanie i załóż pasek dokładnie na środku lewej pięty - idealnie na środku pięty jest środek paska. \nŁokcie szeroko rozpostarte - tak, aby nie przenosić żadnych napięć na barki i na klatkę piersiową. Ręce są silne, przedramiona, bicepsy pracują \nNaciśnij piętą mocno na pasek. Wyprostuj kompletnie nogę w kolanie. Lewa noga jest ustawiona pionowo w górze, prostopadle do ziemi. Prawa stopa prostopadle do ziemi, pięta naciska na podłogę. Cała prawa noga jest rozpłaszczona na ziemi. Kompletnie wyprostowane obie nogi. ", 9,3, R.drawable.supta_padangusthasana,false))
                dao.insert(Asana("Dvi Pada Viparita Dandasana", "Odwrócony kij", "Połóż się na plecach. Zegnij nogi w kolanach i przysuń do pośladków zostawiając niewielką przerwę między nimi. Dłonie oprzyj na ziemi na wysokości głowy tak jak przy przechodzeniu do mostu. Unieś biodra i plecy do góry, ale zamiast wykonywania mostu, oprzyj ciężar ciała na głowie. Ręce zgięte w łokciach połóż za głowę. Następnie unieś głowę, tak aby ciężar ciała spoczywał na zgiętych rękach. Postaraj się powoli jak najbardziej wyprostować nogi.", 2,3, R.drawable.dvi_pada_viparita_dandasana,false))
                dao.insert(Asana("Chakrasana", "Koło dharmy", "Połóż się na plecach. Zegnij nogi w kolanach i przysuń do pośladków zostawiając niewielką przerwę między nimi. Dłonie oprzyj na ziemi na wysokości głowy. Unieś biodra i plecy i przejdź do pozycji  mostu. Następnie postaraj się powoli jak najbardziej wyprostować nogi.", 2,3, R.drawable.chakrasana,false))
                dao.insert(Asana("Eka Pada Chakrasana", "Koło jedną nogą na rękach", "Połóż się na plecach. Zegnij nogi w kolanach i przysuń do pośladków zostawiając niewielką przerwę między nimi. Dłonie oprzyj na ziemi na wysokości głowy. Unieś biodra i plecy i przejdź do pozycji  mostu. Następnie postaraj się powoli jak najbardziej wyprostować nogi. Jedną z nóg unieś do góry. Jeżeli w trakcie unoszenia kolano się zgięło, wyprostuj je. ", 2,3, R.drawable.eka_pada_chakrasana,false))
                dao.insert(Asana("Bhujangasana", "Kobra", "Połóż się płasko na brzuchu, oprzyj czoło na macie, rozluźnij kręgosłup, oddychaj. \nZ wydechem połóż dłonie blisko ciała, po bokach klatki piersiowej, tuż pod barkami. Palce rozszerzone i  skierowane do przodu. Staraj się rozluźnić ciało, oddychaj spokojnie. \nPołóż płasko stopy na ziemi i zbliż do siebie paluchy. Grzbiety stóp dociskaj do maty.\nPodwiń kość ogonową, tym samym kość łonowa powinna wciskać się w podłogę (jeśli jest to niekomfortowe – rozłóż na macie koc) Napnij mocno nogi. \nZ wdechem, zbliż łokcie do tułowia, ściągnij łopatki i odpychając się mocno od dłoni unieś czoło, brodę, klatkę piersiową, ramiona, łokcie i górną część tułowia. Ciało powinno być silne i pełne energii. \nUpewnij się, że cały ciężar ciała nie spoczywa na nadgarstkach. Staraj się unosić dzięki pracy mięśni pleców. Zatrzymaj się w uproszczonej wersji pozycji, w której łokcie są jeszcze ugięte. \nSkieruj wzrok w stronę podłogi, twarz powinna być rozluźniona, a tył szyi wydłużony. Przyciśnij dłonie do podłogi, jednak nie prostuj do końca łokci, powoli, płynnie wyciągaj kręgosłup do góry. Pośladki powinny być rozluźnione. \nUpewnij się, że barki są rozluźnione, przesuwaj je w dół pleców, oddalając tym samym od uszu. Z wydechem powoli wyprostuj ramiona. Możesz delikatnie odchylić szyję i popatrzeć do przodu i do góry. Ale nie odchylaj jej całkiem i nie zadzieraj głowy do sufitu.", 2,3, R.drawable.bhujangasana,false))
                dao.insert(Asana("Camatkarasana", "Dzikość", "Przejdź do bocznej deski na prawą stronę (prawa ręka na ziemi). Lewą nogę przełóż do góry i odłóż do tyłu, za siebie. Pośladki i miednicę skieruj wysoko do góry. Na koniec wyciągnij rękę do góry i za siebie.", 5,3, R.drawable.camatkarasana,false))
                dao.insert(Asana("Eka Pada Vasisthasana", "Boczna deska z nogą do góry", "Przejdź do bocznej deski. Jeśli jesteś stabilny obróć zewnętrznie lewe biodro, zegnij lewe kolano i przytrzymaj duży palec u nogi. Przesuń lewy pośladek w kierunku prawej stopy, jednocześnie obracając na zewnątrz lewe biodro. \nPrzytrzymaj duży palec u nogi lub użyj paska i wyprostuj lewą nogę, utrzymując prawą stronę ciała w linii prostej po przekątnej Najpierw spójrz w dół, aby pomóc sobie w utrzymaniu równowagi, a następnie spójrz przed siebie. Jeśli czujesz, że szyja nie jest napięta, obróć głowę, aby spojrzeć w górę na lewą stopę. ", 1,3, R.drawable.eka_pada_vasisthasana,false))
                dao.insert(Asana("Ashtanga Namaskara", "Kolano klatka podbródek", "Przejdź do klęku podpartego. Zegnij ręce w łokciach i oprzyj klatę piersiową na macie. Na koniec podeprzyj głowę, odkładając podbródek na matę. ", 2,3, R.drawable.ashtanga_namaskara,false))
                dao.insert(Asana("Eka Pada Svanasana", "Jednonogi pies z głową do dołu", "Przejdź do psa z głową do dołu. Unieś jedną z nóg wysoko do góry. Docelowo nogi powinny utworzyć kąt 180 stopni.", 6,3, R.drawable.eka_pada_svanasana,false))
                dao.insert(Asana("Parsva Bakasana", "Boczny żuraw", "Rozpocznij od przysiadu na palcach - złącz kostki i kolana oraz napnij mięśnie ud. \nUnosząc prawą rękę na wdechu do góry staraj się wyciągnąć maksymalnie cały prawy bok tułowia, po czym z wydechem wykonaj skręt w lewo, opierając prawy łokieć o zewnętrzną część lewego uda. Skręt powinien być na tyle mocny, żeby prawa pierś znalazła się za lewym kolanem. \nKluczem do dobrego wykonania pozycji jest osadzenie tricepsa jak najgłębiej na zewnętrznym udzie. \nUmieść teraz dłonie rozstawione na szerokość barków na podłożu. Uwaga! cała powierzchnia dłoni musi równomiernie przylegać do maty! Utrzymując napięte mięśnie w okolicach przepony, zacznij powoli przenosić ciężar ciała do przodu na ręce, patrząc cały czas przed siebie. \nDociśnij lewe udo do górnej części prawego ramienia i unieś stopy z podłoża. W tej pozycji weź co najmniej dwa głębokie oddechy, utrzymuj równowagę i staraj się zachować otwartą klatkę piersiową. ", 1,3, R.drawable.parsva_bakasana,false))
                dao.insert(Asana("Virabhadrasana III", "Wojownik III", "Stań na macie w pozycji wojownika I na prawą stronę. \nDociśnij mocno prawą stopę i z wydechem pochyl tułów i ręcę do przodu w stronę prawego uda. Z wdechem oderwij lewą nogę z podłogi i postaraj się ustawić ręcę, tułów i nogę z tyłu w jednej linii, równolegle do podłoża. \nNoga, na której stoisz może być lekko ugięta, szczególnie jeśli czujesz, że twoje kolana są słabe lub jeśli masz tendencję do przeprostów, noga lewa natomiast niech będzie prosta w kolanie. \nPiętę lewej nogi odciągaj w tył, starając się utrzymać oba pośladki w jednej linii (u większości osób może pojawić się tendencja do skręcenia biodra do góry, wraz z uniesieniem nogi). Z wdechami staraj się rozciągnąć w pozycji ciągnąć mocno rękami do przodu, a lewą nogą w tył. Zachowaj stabilność i spokojny oddech. ", 9,3, R.drawable.virabhadrasana_iii,false))
                dao.insert(Asana("Garudasana", "Orzeł", "Stań prosto. Delikatnie zegnij prawe kolano i owiń lewą nogę wokół prawej, tak aby kolana były ułożone jedna na drugiej. Twoja lewa stopa musi dotykać prawej goleni. \nPodnieś ramiona do wysokości klatki piersiowej i owiń prawą rękę wokół lewej. Upewnij się, że twoje łokcie są wygięte pod kątem 90 stopni. \nOpuść delikatnie biodra, utrzymując równowagę. Twoje kolana muszą poruszać się w kierunku linii środkowej zamiast przechylać się na jedną stronę.", 3,3, R.drawable.garudasana,false))
                dao.insert(Asana("Trikonasana", "Trójkąt", "Stań w Tadasana (pozycja góry). \nPrzejdź do rozkroku, stopy ustaw równolegle. \nNastępnie skręć lewą stopę lekko do wewnątrz a prawą 90′ na zewnątrz. \nUtrzymuj ciężar ciała na obu stopach. \nRęce wyciągnij w bok. \nWykonaj skłon do boku opierając lewą rękę przy lewej stopie, a prawą wyciągnij w górę.", 8,3, R.drawable.trikonasana,false))
                dao.insert(Asana("Prasarita Paddotanasana", "Skłon do przodu z szeroko rozstawionymi nogami", "Stań w Tadasanie (pozycji góry). Rozstaw stopy w szerokim rozkroku, mniej więcej na długość nogi. Palce stóp zwróć lekko do środka, zewnętrzne krawędzie stóp powinny być równoległe. Oprzyj podeszwy stóp mocno o podłogę, starając się docisnąć zewnętrzne krawędzie stóp i wszystkie palce mocno do maty. Ciężar ciała powinien znajdować się pomiędzy stopami. Połóż dłonie na biodrach. Weź głęboki wdech, podwiń kość ogonową i wyciągnij klatkę piersiową do góry. \nNa wydechu przesuń ponownie kość ogonową do tyłu i zegnij tułów do przodu. W tym momencie tułów powinien być równoległy do podłogi. Umieść opuszki palców na podłodze, układając je w odległości barków. Dłonie powinny znajdować się tuż pod ramionami. Wyprostuj ręce, tak aby były prostopadłe do podłogi. Staraj się utrzymać proste plecy, długą wyciągniętą szyję. Głowa patrzy do przodu. \nDociśnij uda do tyłu tym samym wydłużając tułów, oddal od siebie wewnętrzne pachwiny, aby rozszerzyć miednicę. Weź kilka głębokich oddechów. A teraz postaw opuszki palców pomiędzy stopami i na wydechu ugnij łokcie i przenieś tułów niżej, starając się umieścić go między nogami. Jeżeli to możliwe, oprzyj głowę na podłodze. Wtedy możesz nieznacznie zbliżyć stopy do siebie. \nDociśnij aktywnie dłonie do podłogi. Unieś pośladki do góry, rozluźniaj i rozciągaj tylne ścięgna udowe. Wydłużaj przednią część kręgosłupa, w czasie wdechu podnosząc żebra ponad brzuch pogłębiając skłon przy wydechu. Jeżeli twoje ciało jest dobrze rozciągnięte, spróbuj powoli przenosić dłonie na stopy tak, aby były równoległe do nóg. Oddal ramiona od uszu.", 6,3, R.drawable.prasarita_paddotanasana,false))
                dao.insert(Asana("Parivritta Parsvokonasana", "Odwrócony kąt", "Stań w Tadasanie, na wydechu wykonaj skok lub stań w dużym rozkroku. Połóż dłonie na biodrach. Postaw prawą stopę pod kątem 90 stopni względem prawej, a lewą stopę zwróć nieznacznie do środka. Wzmocnij uda, lewe udo obróć na zewnątrz. Rzepka prawego kolana powinna znajdować się w jednej linii z prawą kostką. Na wydechu odwróć tułów w lewą stronę, na kolejnym wydechu zegnij prawe kolano, staraj się aby udo było równolegle do podłogi. Utrzymaj lewą nogę mocną i wyprostowaną.\nWciągnij brzuch i ponownie unosząc klatkę piersiową na wydechu nieznacznie zegnij tułów do przodu. Połóż lewą dłoń na podłodze tuż przy małym palcu prawej stopy ub na . Zwróć klatkę piersiową w lewą stronę, naciskaj lewą ręką na prawą nogę, pacha powinna znajdować się tuż przy kolanie. Prawa dłoń na razie pozostaje jeszcze na kości krzyżowej. \nPodwiń palce tylnej stopy a następnie unieś kolano nad podłogę, wyciągając się od lewej pięty staraj się wyprostować tylną nogę w kolanie i położyć piętę na podłodze. Stopa powinna być nieznacznie skierowana do środka. Znajdź równowagę w tej pozycji. \nSkieruj głowę w kierunku nieba, wyciągaj tylną część szyi. Przesuń prawe ramię nad głowę. Prawy bark powinien znajdować się tuż za głową, twarz skierowana w stronę otwartej pachy. Wyciągnij rękę mocno za siebie, dłoń powinna być otwarta wnętrzem do podłogi. ", 8,3, R.drawable.parivritta_parsvokonasana,false))
                dao.insert(Asana("Utthita Hasta Padangustasana", "Ręka wyciągnięta do dużego palca stopy", "Stań w Tadasanie. \nCiężar ciała przesuń na prawą nogę, dociśnij stopę mocno do podłogi a następnie przy pomocy lewej dłoni unieś lewą zgiętą nogę, tak aby kolano znajdowało się na wysokości brzucha. Prawa dłoń spoczywa na biodrze. \nPalcami lewej ręki uchwyć duży paluch lewej stopy. Wyprostuj nogę, rozciągaj wnętrze stopy, palce stopy skieruj w stronę swojej głowy. W tym czasie prawa noga musi pozostać prosta i aktywna. Rotuj prawe udo do wewnątrz, jego przednią część przesuwaj do tyłu, wciskaj stopę mocno w podłogę. (Jeżeli nie możesz wyprostować nogi, nieznacznie ją zegnij lub użyj paska). \nStaraj się utrzymać biodra w równej linii i proste plecy. Na wdechu wyciągnij boki tułowia do góry, unoś mostek w stronę brody. Oddychaj spokojnie, otwieraj klatkę piersiową. Za każdym razem, kiedy wykonujesz tę pozycję staraj się coraz wyżej unosić nogę. W pełnej pozycji stopa powinna znajdować się na wysokości głowy. \nTeraz przejdź do wariantu z nogą do boku. Trzymając wciąż za palec stopy, przesuń powoli lewą nogę na bok. Rotuj lewe udo na zewnątrz, a prawe do wewnątrz. Prawa noga musi być nadal wyprostowana i aktywna. W tym wariancie biodra nie znajdują się już w jednej płaszczyźnie. Nie przechylaj się na boki, ale wyciągaj do góry, nie rotuj tułowia, ramiona powinny znajdować się w jednej płaszczyźnie. Staraj się unosić stopę coraz wyżej i wyżej, w pełnej pozycji stopa powinna znajdować się na wysokości ramion. Oddychaj jednostajnie, to pomoże ci utrzymać równowagę.", 3,3, R.drawable.utthita_hasta_padangustasana,false))
                dao.insert(Asana("Pasasana", "Pętla", "Stań w Tadasanie. Kucnij, staraj się nie odrywać pięt od podłogi i nie rozłączać kolan, jeśli masz problem z utrzymaniem równowagi, podeprzyj się o ścianę. Trzymaj stopy połączone. Dla ułatwienia możesz podłożyć złożony koc pod piętami. \nZwróć tułów mocno w prawą stronę, oprzyj prawą dłoń na podłodze. Lewą dłoń połóż na prawym udzie. \nPogłębiaj skręt. Pomóż sobie odpychając się lewym łokciem od prawego kolana. Staraj się zbliżyć pięty do podłogi a najlepiej postawić je na podłodze. \nPrzenieś lewy bark do przodu, przed prawe kolano tym samym prawy bark przenieś do tyłu. Unieś do góry klatkę piersiową, wyciągaj kręgosłup do przodu i do góry. Nie rozłączaj kolan. \nOddychaj spokojnie staraj się pogłębić pozycję. Przysuń lewe ramię do przodu, tak aby pacha zetknęła się z kolanem. Zakręć lewą rękę wokół prawej goleni, tak aby dłonią sięgnąć do lewego biodra. Jeżeli nie jest to możliwe, owiń ramię tylko wokół lewego kolana. \nPrzenieś ciężar ciała do przodu. Podnieś prawą rękę z podłogi. Skieruj prawe ramię do tyłu i owiń nim plecy. Połącz dłonie za plecami. Ramiona tworzą pętlę wokół naszego tułowia. Jeżeli nie możesz połączyć dłoni, użyj paska. \nStaraj się nie garbić, ale otwierać klatkę piersiową do prawej strony. Spójrz ponad prawym ramieniem. ", 8,3, R.drawable.pasasana,false))
                dao.insert(Asana("Urdhva Prasarita Eka Padasana", "Szpagat na stojąco", "Stań w Tadasanie. Na głębokim wdechu wyciągaj kręgosłup ku górze. \nNa wydechu zegnij tułów do przodu, połóż dłonie na podłodze, postaraj się zachować przy tym prosty kręgosłup. Jeżeli nie możesz sięgnąć podłogi, nieznacznie zegnij kolana lub wesprzyj dłonie na klockach. \nPrzenieś ciężar ciała na lewą stopę. Cofnij prawą stopę do tyłu, aby zachować lepszą równowagę pozostaw duży paluch na podłodze. \nPrawą dłoń pozostaw na podłodze a lewą umieść na lewej łydce (lub odwrotnie- wersja trudniejsza) .Jeżeli masz trudności z utrzymaniem równowagi możesz pozostawić obie dłonie na podłodze. Powoli zbliżaj tułów do lewego uda. \nNa wdechu unieś prawą nogę, tak aby znajdowała się równolegle do podłogi. Jednocześnie staraj się utrzymać tułów jak najbliżej prawego uda. Noga na której stoisz będzie miała tendencję do rotacji na zewnątrz, unosząc jednocześnie biodro i skręcając miednicę w prawą stronę. Staraj się więc utrzymać miednicę równolegle do podłogi dzięki wewnętrznej rotacji lewego uda. Zwróć uwagę również na kolano lewej nogi, może ono mieć tendencję aby skręcać się do wewnątrz, pilnuj aby rzepka kolanowa skierowana była do przodu. \nMożesz pozostać w pozycji z uniesioną nogą równoległą do podłogi lub pójść dalej, unosząc ją wysoko w stronę sufitu. W tej pozycji obie nogi powinny być proste i znajdować się mniej więcej w jednej linii. Skieruj prawą piętę do sufitu. \nStaraj się coraz bardziej zbliżać przednie żebra do lewego uda, w końcowym etapie zbliżając czoło do lewej goleni.", 6,3, R.drawable.urdhva_prasarita_eka_padasana,false))
                dao.insert(Asana("Kakasana", "Kruk", "Stań w Tadasanie. Przykucnij i rozstaw delikatnie swoje stopy i upewnij się, że pięty są przyklejone do maty. \nRozszerz kolana szerzej niż na szerokość bioder, a tułów umieść pomiędzy udami. \nWyciągnij ręce przed siebie i przyklej dłonie do maty. Rozstaw szeroko palce. \nOprzyj kolana na ramionach tak, żeby znajdowały się jak najbliżej pach. \nDelikatnie stań na palcach i tym samym napnij mocno brzuch. \nWdech, pochyl się delikatnie do przodu, tak aby ciało spoczywało na Twoich zgiętych ramionach. \nWdech. Spróbuj wyprostować ręce i delikatnie unieś swoje stopy.", 1,3, R.drawable.kakasana,false))
                dao.insert(Asana("Bhujapidasana", "Ściśnięte barki", "Stań w Tadasanie. Rozsuń stopy na ok. 60 cm. \nPochyl się do przodu i zegnij kolana. \nPołóż dłonie na podłodze wsuwając je pomiędzy nogi i załóż tylne strony ud na tylne części ramion: pośrodku ramion między barkami a łokciami. \nNa początku umieszczania ud w tej pozycji oderwij pięty od podłogi. \nWykonaj wydech, jeden po drugim oderwij palce stóp od podłogi, balansuj na rękach, po czym skrzyżuj stopy w kostkach. \nDoprostuj ramiona jak najbardziej i unieś głowę do góry. \nPozostań w tej pozycji utrzymując równowagę tak długo, jak nadgarstki będą mogły utrzymać ciężar ciała.", 1,3, R.drawable.bhujapidasana,false))
                dao.insert(Asana("Dandayamana Janushirasana", "Pozycja stojąca głowa do kolan", "Stań w Tadasanie. Spleć palce u dłoni i postaraj się na nie położyć prawą stopę. Lewa noga powinna pozostać prosta.  Spróbuj przesunąć ręce do góry na wysokość głowy, ciągnąc jednocześnie nogę tak, aby wyprostować ją w kolanie", 6,3, R.drawable.dandayamana_janushirasana,false))
                dao.insert(Asana("Parivritta Trikonasana", "Odwrócony trójkąt", "Stań w Tadasanie. Ustaw stopy w rozkroku a następnie obróć prawą stopę na zewnątrz (o 90 stopni) a lewą nieznacznie do wewnątrz (45 stopni). Sprawdź czy pięta prawej nogi znajduje się w jednej linii ze środkową częścią lewej stopy. \nObróć tułów w prawo, wyrównaj biodra, podwiń kość ogonową. \nWyciągaj się do góry. Na wdechu podnieś ramiona do góry i rozłóż je na boki. \nNa wydechu schyl się w stronę prawej nogi. Opuść lewą rękę w kierunku podłogi i połóż koniuszki palców lewej dłoni po zewnętrznej stronie prawej stopy lub na . Nie pozwól żeby tylna pięta oderwała się od podłogi. \nSkręcaj tułów w prawą stronę. Na wydechu wciągnij brzuch i pogłęb skręt tułowia Na każdym wdechu wyciągaj kręgosłup, wydłużaj plecy, poczuj jak rozciąga się klatka piersiowa. Tułów powinien znajdować się równolegle do dłuższej krawędzi maty. \nPodnieś prawą dłoń w kierunku sufitu, tak aby ramiona obu rąk znajdowały się w jednej linii. Wnętrze dłoni skieruj do przodu, dłoń musi być silna i mocno wyciągnięta w górę. \nObróć głowę w prawo i spójrz w kierunku uniesionej dłoni (jeżeli czujesz się niestabilnie w tej pozycji, nie podnoś głowy).", 8,3, R.drawable.parivritta_trikonasana,false))
                dao.insert(Asana("Parsvottanasana", "Piramida", "Stań w wykroku. Biodra skierowane na prosto. Duży paluch nogi dociśnij do maty. Udo powinno być aktywne. \nWykonaj skłon do dołu z bioder (kręgosłup powinien pozostać prosty, a brzuch aktywny). \nDłonie odłóż na macie lub klockach. \nRozluźnij głowę i skieruj twarz do kolana lub do przodu. ", 6,3, R.drawable.parsvottanasana,false))
                dao.insert(Asana("Ustrasana", "Wielbląd", "Uklęknij na macie i połóż dłonie na swoich biodrach.\nUpewnij się, że Twoje kolana i ramiona są w tej samej linii, a podeszwy stóp zwrócone w stronę sufitu.\nOddychaj. Napnij mocno brzuch i podwiń kość ogonową.\nDelikatnie wyginaj swoje plecy i prostuj ręce dotykając swoich stóp.\nOdsuń barki od uszu i nie napinaj szyi.", 2,3, R.drawable.ustrasana,false))
                dao.insert(Asana("Parighasana", "Brama", "Uklęknij na macie i połóż na niej swoje dłonie. \nRozciągnij prawą nogę po prawej stronie. Obróć biodro na zewnątrz tak, aby rzepka skierowana była w stronę nieba. \nWyprostowana noga musi znajdować się w tej samej linii co kolano, a kolano powinno znajdować się tuż pod biodrem tej samej nogi. \nWeź wdech i rozciągnij lewe ramię nad głową, tak aby strona ciała była wysunięta. Twoje ramię musi być przy twoim uchu, a twoje łopatki powinny być mocno przyciśnięte do twoich pleców. \nWydech. Przechyl się na prawą stronę i pozwól, aby prawa ręka spoczywała na twoim udzie, kostce lub stopie. \nPatrz w dal i wyciągnij mocno szyję", 7,3, R.drawable.parighasana,false))
                dao.insert(Asana("Phalakasana", "Deska", "Uklęknij na podłodze z rękami na szerokość barków i przenieś na nie część ciężaru ciała. Dociśnij dłonie mocno do podłogi. Łokcie powinny być wyprostowane, skierowane wnętrzami do góry. \nWyciągnij kręgosłup i tylną część szyi ku górze. Wzrok wbity w podłogę, podbródek powinien być nieznacznie przyciągnięty w kierunku mostka. Wciągnij mięśnie brzucha. \nPodwiń palce stóp i unieś kolana do góry. \nUtrzymaj moce wyprostowane ciało tak, aby znajdowało się w jednej linii. Kontroluj biodra, aby nie zapadały się zbyt nisko. \nMięśnie brzucha cały czas napięte. Nie wypychaj pośladków do góry – w tej pozycji powinny być płaskie. \nBarki powinny znajdować się tuż nad nadgarstkami. Stopniowo staraj się odkręcać ramiona na zewnątrz i napinać mięśnie trójgłowe ramion. \nRozszerz plecy i rozłóż łopatki na boki. Ściągnij pośladki razem. Kość ogonową wyciągnij w stronę pięt.", 9,3, R.drawable.phalakasana,false))
                dao.insert(Asana("Bharadvajasana", "Skręt Bharadvaja", "Usiądź na kocu w dandasanie. \nUgnij nogi jednocześnie kładąc je obok prawego biodra. \nPołóż lewą stopę na prawej. \nKładąc prawą rękę na udzie utrzymuj wyciągnięty w górę kręgosłup. \nDrugą rękę połóż na kostce za plecami. Obie ręce pomagają w skręceniu się.", 8,3, R.drawable.bharadvajasana,false))
                dao.insert(Asana("Upavistha Konasana", "Kąt na siedząco", "Usiądź w Dandasanie a następnie na wydechu przejdź do szerokiego rozkroku. Wyciągnij pośladki na boki. Nogi powinny przylegać do podłogi, dociśnij tyły ud i pięty do podłogi. Mięsnie nóg powinny być napięte. Palce stóp i kolana powinny być skierowana ku górze, nie rozchodzić się na boki, zrotuj uda do wewnętrz. \nUmieść dłonie na podłodze tuż przy biodrach. Unieś klatkę piersiową do góry, jednocześnie pochylając miednicę do przodu. Utrzymaj tę pozycję przez kilka oddechów. \nNa wydechu pochyl się do przodu i chwyć duże palce nóg używając palców wskazujących i środkowych. Ręce powinny być wyprostowane. \nZa każdym wdechem wciągaj pępek do środka, w stronę kręgosłupa. Staraj się schodzić coraz niżej kładąc zbliżając tułów do podłogi. Pamiętaj, że plecy muszą być proste, koryguj je, jeśli się zaokrąglają. Napinaj mięśnie przedniej części ud. Wytrzymaj w pozycji 10 sekund. \nJeżeli masz dobrze rozciągnięte ciało, możesz pójść dalej w stronę podłogi. Na wydechu wyciągaj dalej klatkę piersiową do przodu a następnie opuszczaj ją powoli. Spróbuj położyć głowę na macie i przylgnąć do niej tułowiem. Rozszerzaj klatkę piersiową, utrzymaj proste plecy. Oddychaj spokojnie, stopniowo pogłębiaj pozycję.", 6,3, R.drawable.upavistha_konasana,false))
                dao.insert(Asana("Purvottanasana", "Rozciąganie na wschód", "Usiądź w Dandasanie z dłońmi umieszczonymi z tyłu za biodrami (ok. 15 cm za plecami), palcami skierowanymi w stronę pośladków. \nUgnij kolana i połóż stopy na podłodze. Zwróć duże paluchy do środka. \nNa wydechu dociśnij wewnętrzne części stóp i dłoni do podłogi a następnie unoś biodra do momentu aż tułów będzie znajdował się w jednej linii - przenieś ciężar ciała na dłonie i stopy. Wyprostuj ramiona i podnieś biodra jak najwyżej. Tułów i uda powinny znajdować się w przybliżeniu równolegle do podłogi, a golenie i ramiona niemal prostopadle do podłogi. \nStaraj się utrzymać biodra wysoko, wyprostuj kolejno nogi i oprzyj je palcami o podłogę. Połącz stopy. Unieś znów biodra ale pamiętaj o tym żeby nie napinać pośladków. Wypychaj do góry i rozszerzaj klatkę piersiową, zbliżając do siebie łopatki. \nNie skracając tyłu szyi, nieznacznie odchyl głowę do tyłu patrząc za siebie. Wyciągnij podbródek do góry.", 9,3, R.drawable.purvottanasana,false))
                dao.insert(Asana("Krounchasana", "Czapla", "Usiądź w Dandasanie. Zegnij prawe kolano i przenieś prawą stopę tuż koło biodra, układając ją w pozycji Ardha Virasana. Wyciągnij palce stopy do tyłu, lub lekko do środka. Przy pomocy kciuka przesuń mięśnie łydki na zewnątrz, tak aby zwiększyła się przestrzeń pomiędzy udem a łydką, dzięki temu udo i pośladek będę lepiej dociśnięte do podłogi. Dociśnij palce prawej stopy do podłogi. \nPonownie wyprostuj się i wyrównaj ciężar ciała na obu kościach pośladkowych. Jeżeli czujesz ból w kolanie, możesz podłożyć pod pośladki złożony koc lub piankę. \nUgnij lewe kolano i przyciągnij je blisko tułowia. Złap obiema dłońmi za prawą piętę a następnie wyciągnij lewą nogę do góry i doprostuj ją, wciskaj piętę w stronę podłogi, poczuj jak rozciąga się tylna część uda. Spróbuj utrzymać nogę prostopadle do podłogi. \nZaczynając ruch od kości ogonowej, przez dolne plecy, wyciągaj się do góry. Staraj się wyprostować plecy, nie mogą być wypukłe. Jeżeli możesz, chwyć lewą dłonią za prawy nadgarstek, przyciągaj nogę coraz bliżej. Zbliżaj wciągnięty brzuch w stronę uniesionego uda, spróbuj zbliżyć głowę do goleni.", 7,3, R.drawable.krounchasana,false))
                dao.insert(Asana("Tolasana", "Waga", "Usiądź w pozycji lotosu. \nDłonie ułóż na macie lub na klockach po bokach bioder. \nOdpychając się dłońmi od podłogi lub klocków wyprostuj ręce w łokciach i unieś splecione nogi.", 1,3, R.drawable.tolasana,false))
                dao.insert(Asana("Supta Virasana", "Siodło", "Usiądź w Virasanie. \nPodnosząc biodra, podwijając kość ogonową oprzyj się na przedramionach. \nPołóż plecy na ziemi, wyciągnij ręce za głową. \nDociskaj kolana do podłogi, wydłużaj rękami tułów.", 7,3, R.drawable.supta_virasana,false))
                dao.insert(Asana("Navasana", "Okręt", "Usiądź wygodnie na ziemi, z nogami wyciągniętymi przed Tobą – czyli w Dandasanie. \nPołóż swoje dłonie na podłodze, ugnij delikatnie łokcie. Wyprostuj plecy, wyciągnij kręgosłup, upewnij się, że się nie garbisz. \nOddychaj. Unieś nogi aż do momentu, gdy dotkną brzucha. Podbródek skieruj delikatnie w stronę mostka. Trzymaj pozycję mięśniami brzucha. \nOddychaj. Wyprostuj ręce przed siebie, upewnij się, aby palce dłoni były na wysokości Twojego wzroku, zegnij je w łokciach i ułóż je na tylnej części łydek. Napnij mocno mięśnie brzucha.", 9,3, R.drawable.navasana2,false))
                dao.insert(Asana("Navasana", "Okręt", "Usiądź wygodnie na ziemi, z nogami wyciągniętymi przed Tobą – czyli w Dandasanie. \nPołóż swoje dłonie na podłodze, ugnij delikatnie łokcie. Wyprostuj plecy, wyciągnij kręgosłup, upewnij się, że się nie garbisz. \nOddychaj. Unieś swoje nogi tak, aby były pod kątem 45 stopni. Pamiętaj, aby napinać uda. Podbródek skieruj delikatnie w stronę mostka. Trzymaj pozycję mięśniami brzucha. \nOddychaj. Wyprostuj ręce przed siebie, upewnij się, aby palce dłoni były na wysokości Twojego wzroku i ułóż je równolegle do podłogi. Napnij mocno mięśnie brzucha.", 9,3, R.drawable.navasana,false))
                dao.insert(Asana("Gomukhasana", "Krowi pysk", "Usiądź wygodnie na ziemi, z nogami wyciągniętymi przed Tobą, czyli w Dandasanie. Delikatnie zegnij lewą nogę i umieść ją pod prawym pośladkiem. \nWdech. Ugnij swoją prawą nogę i umieść ją na lewym udzie. \nPowoli usiądź (jeżeli potrzebujesz możesz usiąść na kocu lub kostce). Ułóż swoje ciało w taki sposób, aby kolana były jak najbliżej siebie. \nDelikatnie skręć lewą rękę i ułóż ją za plecami (tak jakbyś chciał przykleić dłoń do pleców). Oddychaj. \nNa wdechu podnieś prawą rękę i ułóż ją za plecami tak, aby dotknąć lewej dłoni (możesz użyć paska, jeżeli Twoje dłonie się nie stykają). \nWyprostuj swoje plecy. Głowa i szyja również proste.", 7,3, R.drawable.gomukhasana,false))
                dao.insert(Asana("Matsyendrasana", "Władca ryb I", "Usiądź z nogami prosto i prostym tułowiem. \nZegnij prawą nogę i przełóż stopę za lewym kolanem. \nZegnij lewą nogę i połóż stopę obok pośladka. \nZłap obiema rękoma za kolano i wyprostuj kręgosłup. \nZrób skręt tułowia i jedną rękę odpychaj się od kolana, a drugą połóż na ziemi.", 8,3, R.drawable.matsyendrasana,false))
                dao.insert(Asana("Kurmasana", "Żółw", "Usiądź z nogami szeroko. Umieść dłonie na udach. \nNa wdechu lekko ugnij kolana. Z wydechem zegnij tułów do przodu i umieść kolejno ręce pod kolanami. \nWsuń ramiona pod kolana wyciągając je na boki i prostując łokcie. Wnętrza dłoni skierowane do dołu. \nOprzyj barki i dłonie na podłodze. \nZ wydechem zegnij tułów jeszcze bardziej do przodu, aż podbródek i klatka piersiowa dotkną podłogi. \nNa końcu zacznij prostować nogi w kolanach.", 6,3, R.drawable.kurmasana,false))
                dao.insert(Asana("Bhekasana", "Żaba", "Ustaw się w pozycji – rozepchnij kolana na zewnątrz możliwie jak najmocniej, pięty ustaw na linii z kolanami pod kątem 90 stopni, biodra wędrują na linię z kolanami. \nWytrzymaj chwilę w tej pozycji po czym zacznij balansować biodrami w tył i przód. \nJeśli ta pozycja nie sprawia Ci kłopotów, zejdź niżej na przedramiona i ponownie zacznij balansować biodrami w tył i przód – pilnuj prostych pleców tzn. nie kompensuj ruchu w odcinku lędźwiowym.", 7,3, R.drawable.bhekasana,false))
                dao.insert(Asana("Shashankasana", "Zając", "Wykonaj pozycję dziecka (Balasana) - uklęknij na macie z połączonymi kolanami, stopy powinny być zwrócone na zewnątrz. Usiądź na piętach i pochyl tułów do przodu, tak aby brzuch spoczywał na udach a czoło na podłodze. Dłonie powinny leżeć koło stóp, zwrócone wnętrzami ku górze. \nChwyć dłońmi za pięty. Podwiń głowę do dołu i przesuń czoło jak najbliżej kolan. Poczuj że twoje ciało opiera się na czubku głowy. (Jeżeli czujesz dyskomfort - podłóż pod głowę złożony koc lub piankę). \nNa wdechu przesuń się do przodu, tak aby pośladki podniosły się do góry a klatka piersiowa odsunęła się od ud.", 6,3, R.drawable.shashankasana,false))
                dao.insert(Asana("Halasana", "Pług", "Wykonaj Sarvangasanę: połóż się na plecach, ręce ułóż wzdłuż tułowia, a dłonie wnętrzami do podłogi. Z wydechem przyciągnij zgięte kolana do tułowia mocno dociśnij i odepchnij się rękoma od podłogi i jednym ruchem unieś biodra. Ugnij łokcie i podeprzyj dolne plecy rękami. Zbliż do siebie oba łokcie tak, by ramiona były równoległe względem siebie. Pozostań w tym ułożeniu na jeden oddech. \nPodczas wydechu ustaw tułów do pionu podpierając się rękoma tak, by broda dotknęła klatki piersiowej; wyprostuj nogi. Palce stóp skieruj do sufitu.\nWydech – uwolnij ucisk brody na mostek, lekko opuść tułów, zegnij nogi w kolanach i postaw grzbiety stóp na podłogę (lub palce stóp) za głową. Wyprostuj nogi, naciskaj dłońmi na środek pleców tak, by tułów był prostopadły do podłogi. \nWdech – wyprostuj ręce, spleć razem palce i dociśnij je do podłogi. Teraz wnętrza łokci są skierowane w stronę sufitu. Oddalaj ramiona od uszu, a nogami sięgaj jak najdalej w przeciwnym kierunku – dzięki temu kręgosłup prostuje się. ", 9,3, R.drawable.halasana,false))
                dao.insert(Asana("Uttana Pristhasana", "Jaszczurka", "Zacznij w niskim wykroku z lewym kolanem z przodu, dłonie ułóż na macie. Obróć lewą stopę na zewnątrz pod kątem 45 stopni i przetocz ją na zewnętrzną krawędź. Lekko dociśnij wewnętrzną część lewego uda. ", 7,3, R.drawable.uttana_pristhasana,false))
                dao.insert(Asana("Salamba Sirsasana", "Stanie na głowie z podparciem", "Złóż matę na 2,3 części i uklęknij przed nią. Połóż swoje przedramiona na podłodzę i rozszerz łokcie mniej więcej na szerokość barków. \nDelikatnie odepchnij ramiona na zewnątrz i mocno naciśnij nadgarstki na podłodze. Spleć mocno dłonie. \nOprzyj głowę delikatnie na macie. Pamiętaj, aby Twoja szyja była wyciągnięta. Następnie oprzyj czubek głowy na macie i ułóż ją pomiędzy swoimi dłońmi. Pamiętaj, aby zachować pomiędzy nimi odstęp. \nPodnieś delikatnie kolana i idź powoli w kierunku swoich łokci. Unieś swoje uda tak, aby ciało tworzyło odwróconą literkę”V”. Dzięki temu przedłużysz przednią część ciała i odciążysz szyję. \nUnieś delikatnie swoje nogi z podłogi (ważne, aby stopy podnieść razem). Ułóż nogi prostopadle do podłogi i przymocuj kość ogonową do tylnej części miednicy. Pchnij piętami w kierunku sufitu. Twoje ciało powinno być wyrównane w linii prostej. \nTrzymaj wagę zrównoważoną na obu przedramionach. ", 3,3, R.drawable.salamba_sirsasana,false))
                dao.insert(Asana("Marichyasana", "Mędrzec", "Usiądź w Dandasanie. Zegnij prawą nogę w kolanie i przysuń ją do prawego pośladka. Lewą rękę zegnij w łokciu i zahacz ramię o prawe kolano. Prawą rękę odłóż na macie za swoimi plecami. Wykonaj skręt tułowia w prawą stronę i skieruj głowę w stronę ręki znajdującej się za plecami.", 8,3, R.drawable.marichyasana,false))
                dao.insert(Asana("Raja Kapotasana", "Król gołębi", "Brak opisu", 2,4, R.drawable.raja_kapotasana,false))
                dao.insert(Asana("Padma Sarvangasana", "Lotos na ramionach", "Połóż się na plecach i przejdź do świecy.  Przy pomocy tylko jednej z rąk naraz, ułóż nogi w pozycji lotosu. Wróć rękami do podpierania pleców jak w pozycji świecy, żeby ustabilizować pozycję. Przenieć dłonie na kolana. ", 7,4, R.drawable.padma_sarvangasana,false))
                dao.insert(Asana("Eka Pada Chakrasana II", "Koło jedną nogą na przedramionach", "Przejdź do koła na przedramionach. Przenieś jedną nogę do góry.", 2,4, R.drawable.eka_pada_chakrasana_ii,false))
                dao.insert(Asana("Salamba Sirsasana Pada Garudasana", "Orzeł na przedramionach", "Przejdź do Pinch Mayurasany i zapleć nogi jak w pozycji orła. ", 3,4, R.drawable.salamba_sirsasana_pada_garudasana,false))
                dao.insert(Asana("Baddha Kona Sirsasana", "Spętany kąt", "Przejdź do Pinch Mayurasany. Kiedy złapiesz równowagę ułóż nogi jak w pozycji odpoczywającej bogini.", 3,4, R.drawable.baddha_kona_sirsasana,false))
                dao.insert(Asana("Sirsasana", "Stanie na głowie", "Ręce oparte są dłońmi w ten sposób, aby dłonie i głowa tworzyły trójkąt równoramienny. Ciężar ciała powinien być rozłożony równomiernie na ręce i głowę, jednakże zachowanie równowagi zależne jest bardziej od ciężaru spoczywającego na rękach. Tułów jest wyprostowany w stawach biodrowych.", 3,4, R.drawable.sirsasana,false))
                dao.insert(Asana("Eka Pada Sirsasana", "Jednonogie stanie na głowie", "Ręce oparte są dłońmi w ten sposób, aby dłonie i głowa tworzyły trójkąt równoramienny. Unieś tylko jedną nogę/ Ciężar ciała powinien być rozłożony równomiernie na ręce, głowę i palce nogi pozostawionej na ziemi, jednakże zachowanie równowagi zależne jest bardziej od ciężaru spoczywającego na rękach. Tułów jest wyprostowany w stawach biodrowych. ", 3,4, R.drawable.eka_pada_sirsasana,false))
                dao.insert(Asana("Ardha Chakrasana", "Półkole", "Rozpocznij od pozycji rozciągniętego trójkąta (Utthita Trikonasana) zaczynając od prawego boku. \nPołóż lewą dłoń na biodrze. Na wdechu zegnij lekko kolano i połóż opuszki palców prawej dłoni na podłodze lub na tak aby znajdowały się w odległości ok.30-50cm stopy. \nOderwij lewą stopę na od podłogi i przysuń ją do prawej, tak aby ciężar ciała spoczywał na ugiętej prawej nodze. Staraj się oddychać spokojnie by utrzymać równowagę. \nDociśnij prawą stopę do podłogi. Na wydechu wyprostuj prawą nogę, tym samym lewą nogę unieś do góry. Lewa noga powinna być równoległa do podłogi, lub trochę powyżej. Utrzymaj uniesioną nogę silną i mocno wyciągniętą. Pilnuj aby rzepka kolanowa nie przekręcała się. \nZrotuj górną część tułowia w lewą stronę. Klatka piersiowa powinna być odwrócona przodem do ściany. Wydłużaj kość ogonową w stronę uniesionej stopy. \nStaraj się utrzymać równowagę, kiedy czujesz się stabilnie, wyciągnij lewą rękę do góry, tak aby była przedłużeniem prawej ręki. Spójrz do góry w stronę wyciągniętej w górę dłoni. ", 2,4, R.drawable.ardha_chakrasana,false))
                dao.insert(Asana("Akarna Dhanurasana", "Strzała", "Siądź w Dandasanie. Przejdź do skłonu i chwyć duże paluchy u stóp. \nJedną z nóg podciągnij za paluch do tyłu, tak żeby kolano przekroczyło linię tułowia. \n Wyobraź sobie, że naciągasz cięciwę łuku. W podobny sposób postaraj się przyciągnąć nogę.", 7,4, R.drawable.akarna_dhanurasana,false))
                dao.insert(Asana("Natarajasana", "Król tańca", "Stań w Tadasanie (Pozycji góry). Na wdechu przenieś ciężar ciała na lewą stopę. Zegnij prawą nogę i przesuń kolano do tyłu, tak aby stopa znalazła się tuż za pośladkiem. Zwróć prawą pietę do sufitu. Lewa noga pozostaje silna i wyprostowana, lewe udo wypychaj nieznacznie do tyłu, dociśnij mocno lewą stopę do podłogi. Staraj się utrzymać równowagę. \nSięgnij do tyłu prawą dłonią i chwyć nią środkową część prawej stopy. Chwyć duży palec prawej stopy. Aby uniknąć ściągnięcia w dolnej części pleców, unoś kość łonową w stronę mostka, jednocześnie wciskając kość ogonową w stronę podłogi. \nOdwróć prawy łokieć na zewnątrz i skieruj go ku górze. Wyciągnij prawą rękę poza głowę, przesuń prawą stopę bliżej tyłu głowy. Zacznij unosić prawą stopę do góry, oddalając ją jednocześnie od pleców. Wyciągnij za sobą lewe udo, powinno znajdować się mniej więcej równolegle do podłogi. Nie unoś prawego biodra, powinno pozostać w tym samym miejscu. \nWyciągnij lewe ramię do przodu, równolegle do podłogi. Place dłoni wyciągnij w stronę sufitu, lub połącz opuszki palców: wskazującego i kciuka.", 3,4, R.drawable.natarajasana,false))
                dao.insert(Asana("Urdhva Prasarita Eka Padasana", "Szpagat przy ścianie", "Stań w Tadasanie. Na głębokim wdechu wyciągaj kręgosłup ku górze. \nNa wydechu zegnij tułów do przodu, połóż dłonie na podłodze, postaraj się zachować przy tym prosty kręgosłup. Jeżeli nie możesz sięgnąć podłogi, nieznacznie zegnij kolana lub wesprzyj dłonie na klockach. \nPrzenieś ciężar ciała na lewą stopę. Cofnij prawą stopę do tyłu, aby zachować lepszą równowagę pozostaw duży paluch na podłodze. \nPrawą dłoń pozostaw na podłodze a lewą umieść na lewej łydce (lub odwrotnie- wersja trudniejsza) .Jeżeli masz trudności z utrzymaniem równowagi możesz pozostawić obie dłonie na podłodze. Powoli zbliżaj tułów do lewego uda. \nNa wdechu unieś prawą nogę po ścianie, tak aby znajdowała się równolegle do podłogi. Jednocześnie staraj się utrzymać tułów jak najbliżej prawego uda. Noga na której stoisz będzie miała tendencję do rotacji na zewnątrz, unosząc jednocześnie biodro i skręcając miednicę w prawą stronę. Staraj się więc utrzymać miednicę równolegle do podłogi dzięki wewnętrznej rotacji lewego uda. Zwróć uwagę również na kolano lewej nogi, może ono mieć tendencję aby skręcać się do wewnątrz, pilnuj aby rzepka kolanowa skierowana była do przodu.", 7,4, R.drawable.urdhva_prasarita_eka_padasana2,false))
                dao.insert(Asana("Padangusthasana", "Skłon do przodu z uchwytem dużych paluchów", "Stań w Tadasanie. Rozstaw stopy na szerokość bioder, tak aby wewnętrzne stopy były równolegle do siebie. Napnij mięśnie ud tak aby unieść rzepki kolanowe do góry. Nogi muszą być całkowicie proste, skieruj kość ogonową do dołu, schowaj pośladki. Dociśnij podeszwy stóp do podłogi. \nNa wydechu wyciągnij ręce do góry, wydłużaj tułów a następnie zegnij go, zaczynając od ruchu bioder, przenosząc jednocześnie tułów i głowę do przodu. \nPodwiń środkowy i wskazujący palec w każdej dłoni i umieść je pod dużymi paluchami stóp (możesz ująć także drugi palec) a następnie mocno uchwyć palcami dłoni paluchy stóp, od góry połącz je z kciukami. Dociskaj paluchy do ziemi. Jeżeli nie możesz dosięgnąć paluchów stóp, pod każdą ze stóp umieść pasek i trzymaj je w jak najbliższej odległości od podłogi. \nNa wdechu unieś tułów, tak jakbyś chciał(a) ponownie wstać, prostując jednocześnie łokcie. Wydłużaj tułów i na następnym wydechu, unieś kości pośladkowe. W zależności od twojej giętkości dolne plecy utworzą wgłębienie w mniejszym lub większym stopniu. Teraz możesz rozluźnić ścięgna udowe i wciągnąć brzuch, delikatnie unosząc go w stronę tylnej części miednicy. \nUnoś górny mostek tak wysoko jak tylko możesz, ale uważaj aby nie unieść jednocześnie zbyt mocno głowy, gdyż to może skrócić tył szyi. Twarz powinna być zrelaksowana. \nNa wdechach unoś tułów a na wydechach unoś kości pośladkowe. W ten sposób zwiększy się zagłębienie w dolnej części pleców. \nNa kolejnym wydechu, ugnij łokcie, kierując je na zewnątrz. Dociskaj stopy mocno do podłogi. Wydłużaj boki tułowia. Delikatnie pogłębiaj skłon, przyciągając tułów w stronę nóg. \nJeżeli twoje ścięgna są dobrze rozciągnięte, możesz zbliżyć czoło do goleni. Jeżeli masz z tym problem, lub bolą cię dolne plecy, lepiej pozostań we wcześniejszej pozycji, skupiając się na wydłużaniu tułowia.", 6,4, R.drawable.padangusthasana,false))
                dao.insert(Asana("Uddiyana Bandha", "Blokada", "Stań w Tadasanie. Ułóż stopy szerzej niż biodra. Ugnij kolana. Ręce oprzyj o kolana i zablokuj w łokciach. Pochyl się. Zrób wydech. Na bezdechu pochyl brodę do mostka i na bezdechu wciągnij brzuch. ", 4,4, R.drawable.uddiyana_bandha,false))
                dao.insert(Asana("Parivrtta Surya Yantrasana", "Kompas", "Usiądź na macie. Nogi jak w Janu Sirsasana A. Prawa noga wyprostowana, lewa zgięta w kolanie, stopa przyklejona do wewnętrznej części uda prawej nogi. \nUgnij prawą nogę w kolanie, postaw stopę na macie. Pochyl klatkę piersiową i przenieś prawą rękę między udo i łydkę prawej nogi. Chwyć za kostkę po jej zewnętrznej stronie. \nChwyć za kostkę drugą ręką i unieś nogę. Połóż ją sobie na barku – im wyżej, tym lepiej. \nWyprostuj prawą rękę i oprzyj dłoń na podłodze, blisko pośladka, tak żeby zablokować nogę w górze. \nLewą ręką chwyć za zewnętrzną krawędź stopy i płynnym ruchem wyprostuj nogę, wypychając klatkę piersiową do przodu wydłużając przy tym kręgosłup.", 7,4, R.drawable.parivrtta_surya_yantrasana,false))
                dao.insert(Asana("Pinch Mayurasana", "Paw", "Usiądź w siadzie japońskim. Zegnij ręce w łokciach i odłóż ją na matę. Następnie wybij się nogami do góry.", 3,4, R.drawable.pinch_mayurasana,false))
                dao.insert(Asana("Naginyasana", "Syrena", "Ustaw prawą nogę z przodu, biodra na jednym poziomie. Goleń tylnej nogi oprzyj pionowo o ścianę i wyprostuj plecy (dla ułatwienia ułóż kostki po bokach bioder i przez chwilę wyciągaj kręgosłup do sufitu, aby przyzwyczaić ciało do pracy). \nZ kolejnym wdechem włóż tylną stopę w środek lewego łokcia, jeżeli nie sięgasz użyj paska zakładając pętlę na stopę i łokieć. \nPołącz dłonie, po kilku oddechach podnieś obydwie ręce do góry i przesuń do tyłu tak, aby tył głowy swobodnie oparł się o Twoje przedramię.", 7,4, R.drawable.naginyasana,false))
                dao.insert(Asana("Adho Mukha Vrksasana", "Stanie na rękach", "Wykonaj pozycję Psa z głową do dołu, ułóż dłonie 15 cm od ściany. Dłonie muszą być rozsunięte na szerokość barków, palce skierowane w stronę ściany. Dłonie muszą leżeć symetrycznie. Przesuń się do przodu, tak aby barki znalazły się ponad palcami. \nZdecyduj, która noga będzie nogą dominującą, z której wykonasz wyskok. Ugnij drugą nogę do góry, przyciągając piętę do pośladka. \nNa wdechu zegnij dominującą nogę i podskocz na niej do góry. Możesz podskoczyć kilkakrotnie przymierzając się do skoku. Kiedy będziesz już gotowy, a dominująca noga będzie w górze, w tym samym czasie przesuń ku sufitowi drugą nogę. Unieś jednocześnie biodra do góry. Kiedy dotkniesz piętą ściany, dołącz drugą nogę po czym wyprostuj je. Złącz stopy i wyciągnij pięty ku górze. \nPamiętaj o miarowym oddechu. Dociskaj palce do podłogi , trzymaj ramiona wyprostowane. Nie kręć głową, skup spojrzenie w jednym miejscu, np. pomiędzy dłońmi, lub nieco przed nimi. \nSpróbuj delikatnie odsunąć pięty od ściany, rozciągnąć plecy, jednocześnie żebra ściągając do środka. Pracuj nad równowagą.", 2,4, R.drawable.adho_mukha_vrksasana,false))
                dao.insert(Asana("Visvamitrasana", "Małpia boczna deska", "Brak opisu", 1,5, R.drawable.visvamitrasana,false))
                dao.insert(Asana("Parsva Bakasana II", "Żuraw", "Brak opisu", 8,5, R.drawable.parsva_bakasana2,false))
                dao.insert(Asana("Urdhva Mukha Paschimottanasana", "Skłon z twarzą do góry", "Połóż się na plecach z wydechem. \nWdech – unieś złączone nogi do góry, na wydechu przerzuć je za głowę i chwyć palcami wskazującymi i środkowymi za paluchy obu stóp. \nNa wdechu nie puszczając palców jednym ruchem usiądź na pośladkach z nogami skierowanymi do góry, spójrz na stopy, wydłuż kręgosłup, oddal ramiona od uszu. Możesz lekko ugiąć kolana, aby lepiej kontrolować ruch i nie upaść nogami prosto na podłogę. Będzie to z pewnością wymagało kilku prób z twojej strony. Gdy już się uda pozostań na 5 oddechów z nogami u góry.", 9,5, R.drawable.urdhva_mukha_paschimottanasana,false))
                dao.insert(Asana("Makara Adho Mukha Svanasana", "Deska - delfin", "Połóż się na podłodze na brzuchu. \nUłóż ramiona tak, aby łokcie były w prostej linii z barkami; dłonie złożone razem, palce rozsunięte, głowa nad dłońmi. \nOprzyj się mocno na przedramionach (od nadgarstka do łokcia); podnieś plecy oraz nogi aż ciało wyprostuje się i plecy będą w prostej linii z nogami. \nRozciągnij obojczyki i rozluźnij barki lekko do tyłu. \nRozluźnij kark, szyję, patrz w jeden punkt na podłodze.", 9,5, R.drawable.makara_adho_mukha_svanasana,false))
                dao.insert(Asana("Devaduuta Panna Asana", "Upadły anioł", "Przejdź do bocznego kruka. Następnie oprzyj głowę bokiem o matę i wyprostuj zewnętrzną nogę do góry. Drugą pozostaw opartą na ramieniu. ", 1,5, R.drawable.devaduuta_panna_asana,false))
                dao.insert(Asana("Eka Pada Koundinyanasana II", "Płotkarz II", "Przejdź do psa z głową do dołu z uniesioną nogą. Następnie kolano uniesionej nogi przenieś na bok. Pociągnij nogę jeszcze bardziej do przodu. Oprzyj udo na ramieniu. Wyprostuj nogę w kolanie, a drugą unieś do góry.", 1,5, R.drawable.eka_pada_koundinyanasana_ii,false))
                dao.insert(Asana("Sirsasana II Padmasana", "Stanie na głowie II", "Przejdź do stania na głowie. Jeśli utrzymujesz już równowagę spróbuj zapleść nogi jak w lotosie. ", 3,5, R.drawable.sirsasana_ii_padmasana,false))
                dao.insert(Asana("Taraksvasana", "Skorpion na rękach", "Przejdź do stania na rękach. Kiedy złapiesz równowagę postaraj się powoli zbliżyć stopy do głowy.", 1,5, R.drawable.taraksvasana,false))
                dao.insert(Asana("Pada Garudasana", "Orzeł na rękach", "Przejdź do stania na rękach. Kiedy złapiesz równowagę, spróbuj zapleść nogi jak do pozycji orła.", 1,5, R.drawable.pada_garudasana,false))
                dao.insert(Asana("Eka Hasta Vrksasanav", "Jednorękie drzewo", "Przejdź do stania na rękach. Po złapaniu równowagi postaraj się wykonać rozkrok w powietrzu. Na koniec unieś jedną z rąk do góry.", 1,5, R.drawable.eka_hasta_vrksasanav,false))
                dao.insert(Asana("Gandha Bherundasana", "Twarz budząca grozę", "Przejdź do stania na rękach. Ugnij lekko ręce w łokciach. ", 1,5, R.drawable.gandha_bherundasana,false))
                dao.insert(Asana("Tittibhasana", "Świetlik", "Stań w lekkim rozkroku, kolana i stopy rozstaw szerzej niż na szerokość bioder. \nSięgnij między nogami i chwyć dłońmi tylną część kostek, przenieś barki pod kolana. \nDłonie ułóż na macie, za piętami z palcami skierowanymi ku piętom. Ugnij kolana, a następnie tworząc podporę z ramion siądź na górnej części ramion (możesz lekko ugiąć łokcie). \nPrzyciśnij uda do barków i spoglądaj przed siebie, unieś pięty z podłogi i wyprostuj nogi.", 1,5, R.drawable.tittibhasana,false))
                dao.insert(Asana("Ardha Baddha Padmottanasana", "Skłon do przodu w pół-lotosie", "Stań w Tadasanie. Przenieś ciężar ciała na lewą stronę, podnieś prawą nogę, zegnij prawe kolano i umieść podeszwę prawej stopy na lewym udzie (w półlotosie). \nPrzyciągnij piętę wysoko do krocza. Utrzymuj prawe biodro otwarte, pachwinę rozluźnioną, prawe kolano kieruje się do ziemi. Brzuch jest wciągnięty a kręgosłup wyprostowany.\nTrzymając się lewą ręką za prawą stopę, przenieś prawą rękę za plecy i chwyć paluch prawej stopy palcami prawej ręki: wskazującym, środkowym i kciukiem.\nZwolnij lewą dłoń. Wykonaj wydech i skłon do przodu, połóż lewą dłoń na podłodze obok lewej stopy i trzymaj kręgosłup wklęsły. Wykonaj parę oddechów. Jeśli jesteś początkujący połóż dwie dłonie na podłodze (bez chwytu za paluch stopy w półlotosie). \nWykonaj wydech i połóż czoło na lewym kolanie.Jeśli nie jesteś w stanie położyć całej dłoni na podłodze, zacznij od koniuszków palców lub oprzyj dłoń na kostce.", 9,5, R.drawable.ardha_baddha_padmottanasana,false))
                dao.insert(Asana("Bhairavasana", "Niszczyciel świata", "Usiądź na ziemi z wyprostowanymi nogami. Unieś lewą nogę i umieść ją za prawym barkiem. Połóż obie ręce na podłodze. \nOdepchnij się rękoma i wstań na prawej nodze. \nWyciągnij prawą nogę do boku, a obie ręce przenieś na lewą stronę. Podnieś prawą rękę.", 9,5, R.drawable.bhairavasana,false))
                dao.insert(Asana("Astavakrasana", "Osiem kątów", "Usiądź w Dandasanie. Prawą nogę zegnij w kolanie i przysuń do tułowia. Złap ją prawą ręką i unieś do góry. Przełóż nogę do lewej ręki i prawą rękę przełóż na zewnętrzną część łydki. Za pomocą siły rąk odłóż nogę na prawy bark i ściągnij ją maksymalnie nisko. Prawą rękę odłóż na matę (noga dalej podparta na tej ręce). Odstaw drugą rękę na matę. Unieś ciało na rękach. Unieś bardziej lewą nogę, zegnij ją i odłóż lewą stopę na prawą. Wyprostuj obie nogi. Pochyl się do przodu zginając ręce.", 1,5, R.drawable.astavakrasana,false))
                dao.insert(Asana("Eka Pada Rajakapotasana II", "Jednonogi król gołębi II", "Usiądź w Dandasanie. Ugnij prawe kolano i umieść stopę na podłodze, dokładnie przed prawym pośladkiem. Goleń powinna być w przybliżeniu prostopadła do podłogi. \nNastępnie przesuń się nieznacznie w prawo i wyciągnij lewą prostą nogę z tyłu za tułowiem. Połóż ją na podłodze w pełni rozciągniętą, z wierzchem nogi i szczytami palców na podłodze. \nUgnij lewe kolano i unieś goleń mniej więcej prostopadle do podłogi. Ciężar twojego ciała będzie balansował na prawej stopie i lewym kolanie (a także kości udowej, jeśli twoje ciało jest dobrze rozciągnięte). Aby ustabilizować pozycję, przyciśnij prawe kolano do przodu, aż będzie wystawało nieco poza prawe palce u stopy. \nNa wdechu wyciągnij do góry prawe ramię, ugnij łokieć i przy wydechu chwyć lewą stopę. Następnie zrób to samo z lewym ramieniem. Trzymając mocno stopę, unieś klatkę piersiową i odchyl głowę do tyłu w kierunku podeszwy lewej stopy. Naciskaj łokcie kierując je w stronę sufitu.", 2,5, R.drawable.eka_pada_rajakapotasana_ii,false))
                dao.insert(Asana("Mayurasana", "Paw", "Usiądź w siadzie japońskim. Rozszerz nogi. Zegnij ręce i połóż łokcie na brzuchu. Pochyl się do przodu, aż dłonie oprą się na macie.  Przesuń nogi nieco do tyłu i unieś je do góry. Twoim jedynym punktem podparcia powinny pozostać dłonie. Wyprostuj nogi w kolanach", 1,5, R.drawable.mayurasana,false))
                dao.insert(Asana("Eka Pada Rajakapotasana I", "Jednonogi król gołębi I", "Usiądź wygodnie w siadzie skrzyżnym, wyprostuj plecy. Prawą goleń ułóż na lewym, tak by były równoległe do miednicy i przylegały do podłogi. Delikatnie przesuń prawe kolano do przodu, tak aby znajdował się tuż za twoim prawym nadgarstkiem. Kiedy to robisz, połóż prawą stopę pod tułowiem i postaw prawą stopę przed lewym kolanem. Zewnętrzne strony prawej goleni muszą spoczywać na podłodze. \nPowoli przesuń lewą nogę do tyłu. Wyprostuj kolano i upuść przód Twojego uda na podłogę. Ułóż prawy pośladek na podłodze. Połóż prawą piętę przed lewym biodrem. \nUłóż biodra w jednej linii, pięta prawej nogi dotyka lewej pachwiny. Wyciągnij do góry kręgosłup, odpychając się dłońmi od bioder. Wypnij klatkę piersiową, odchylając głowę. Zegnij lewą nogę i złap stopę lewą ręką, przyciągając ją w kierunku głowy \nZ wydechem chwyć drugą ręką za stopę. Umieść dłonie na kostce lewej nogi i przyciągnij ją jeszcze bardziej w stronę tułowia. Z wdechem rozszerz i bardzo mocno wypchnij klatkę piersiową. \nZ wdechem rozszerz i bardzo mocno wypchnij klatkę piersiową. Dotknij czubkiem głowy podeszwy stopy.", 7,5, R.drawable.eka_pada_rajakapotasana_i,false))
                dao.insert(Asana("Eka Pada Koundinyanasana I", "Płotkarz I", "Wejdź do pozycji analogicznie jak do Eka Pada Koundinyanasana II, z tą różnicą, że druga noga powinna utworzyć ze skierowaną do przodu kąt 180 stopni, a nie prostopadle. ", 1,5, R.drawable.eka_pada_koundinyanasana_i,false))
                dao.insert(Asana("Karnapidasana", "Ściśnięte uszy", "Wykonaj Halasanę: rozpoczynając od leżenia na plecach ręce ułóż wzdłuż tułowia a dłonie wnętrzami do podłogi. Z wydechem przyciągnij zgięte kolana do tułowia, podnieś biodra z podłogi, ugnij łokcie i podeprzyj dolne plecy rękami. \nPodczas wydechu ustaw tułów do pionu popychając plecy rękami- broda dotyka klatki piersiowej. \nWydech: postaw grzbiety stóp na podłogę (lub palce stóp). Wyprostuj nogi, naciskaj dłońmi na środek pleców tak, by tułów był prostopadły do podłogi. Ten etap to Halasana z podparciem. \nNastępnie z wydechem ugnij kolana i umieść je przy uszach. \nWyprostuj ręce, spleć razem palce i dociśnij je do podłogi. \nKolana spoczywają na podłodze, dotykają ramion i naciskają lekko na uszy. Całe grzbiety stóp dotykają podłogi. ", 2,5, R.drawable.karnapidasana,false))

